import React, { useEffect, useRef, useState } from 'react';
import { motion } from 'framer-motion';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronLeft, faChevronRight, faAngleDown } from '@fortawesome/free-solid-svg-icons';
import './HeroSection.css';

const HeroSection = () => {
  const carouselRef = useRef(null);
  const containerRef = useRef(null);
  const [isHovered, setIsHovered] = useState(false);
  const [rotationSpeed, setRotationSpeed] = useState(1);
  const [wheelActive, setWheelActive] = useState(false);
  const [spinClass, setSpinClass] = useState('spin-medium');

  // Food images for the carousel
  const images = [
    "https://images.unsplash.com/photo-1547592180-85f173990554?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    "https://images.unsplash.com/photo-1504674900247-0877df9cc836?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    "https://images.unsplash.com/photo-1540189549336-e6e99c3679fe?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    "https://images.unsplash.com/photo-1565299624946-b28f40a0ae38?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    "https://images.unsplash.com/photo-1546069901-ba9599a7e63c?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    "https://images.unsplash.com/photo-1563379926898-05f4575a45d8?ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
  ];

  // Handle smooth scroll to sections
  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      window.scrollTo({
        top: section.offsetTop - 80,
        behavior: 'smooth'
      });
    }
  };

  // Handle scroll events to control carousel rotation speed
  useEffect(() => {
    const handleWheel = (e) => {
      if (e.deltaY > 0) { // Scrolling down
        setRotationSpeed(prev => Math.min(prev + 0.5, 3)); // Increase speed up to 3x
        setWheelActive(true);
        setSpinClass('spin-fast');

        // Reset wheel active state after a delay
        setTimeout(() => {
          setWheelActive(false);
        }, 1000);
      }
    };

    window.addEventListener('wheel', handleWheel);

    // Reset rotation speed gradually when wheel is inactive
    const resetInterval = setInterval(() => {
      if (!wheelActive && rotationSpeed > 1) {
        setRotationSpeed(prev => Math.max(1, prev - 0.2));
        setSpinClass('spin-medium');
      }
    }, 2000);

    return () => {
      window.removeEventListener('wheel', handleWheel);
      clearInterval(resetInterval);
    };
  }, [wheelActive, rotationSpeed]);

  return (
    <section id="hero" className="relative bg-gradient-to-r from-gray-50 to-white min-h-screen overflow-hidden section-fade" ref={containerRef}>
      {/* Content Container */}
      <div className="container mx-auto px-4 md:px-6 py-16 md:py-0 h-full relative z-10 flex items-center">
        <div className="flex flex-col lg:flex-row items-center justify-between w-full">
          {/* Left Side Content */}
          <motion.div 
            className="w-full lg:w-1/2 space-y-6 text-gray-900 text-center lg:text-left mb-12 lg:mb-0"
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, ease: [0.22, 1, 0.36, 1] }}
          >
            <div className="inline-block px-4 py-1.5 bg-primary text-white text-sm font-semibold rounded-full animate-fade-in">
              FREE DELIVERY
            </div>
            <h1 className="text-3xl md:text-4xl lg:text-5xl xl:text-6xl font-bold leading-tight">
              Food Sorted.<br/>
              <span className="text-primary">Life's Sorted.</span>
            </h1>
            <p className="text-base md:text-lg lg:text-xl font-light max-w-xl mx-auto lg:mx-0 text-gray-600">
              Delicious home-style meals delivered to your doorstep. 
              With us you no longer have to run a kitchen, manage groceries or worry about your daily food.
            </p>
            <div className="flex flex-wrap gap-4 pt-4 justify-center lg:justify-start">
              <button 
                onClick={() => scrollToSection('try-meal')}
                className="px-5 py-2.5 sm:px-6 sm:py-3 bg-accent hover:bg-orange-600 text-white font-medium rounded-full shadow-lg hover:shadow-xl transition-all transform hover:-translate-y-1"
              >
                Try a Meal
              </button>
              <button
                onClick={() => scrollToSection('daily-menu')}
                className="px-5 py-2.5 sm:px-6 sm:py-3 bg-white border border-gray-300 hover:border-gray-400 text-gray-800 font-medium rounded-full shadow-md hover:shadow-lg transition-all transform hover:-translate-y-1"
              >
                Explore Menu
              </button>
            </div>
          </motion.div>

          {/* 3D Image Carousel */}
          <div
            className="w-full lg:w-1/2 perspective-1000 h-[350px] sm:h-[400px] md:h-[450px] lg:h-[500px]"
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
          >
            <div 
              ref={carouselRef}
              className={`relative w-full h-full transform-style-3d carousel-container ${!isHovered ? `spin-animation ${spinClass}` : ''}`}
            >
              {images.map((img, index) => {
                // Calculate position for each panel in 3D space
                const theta = ((2 * Math.PI) / images.length);
                const angle = theta * index;
                const radius = 250; // Reduced radius for better mobile display

                return (
                  <motion.div
                    key={index}
                    className="carousel-panel absolute w-full h-full"
                    style={{
                      transform: `rotateY(${angle}rad) translateZ(${radius}px)`,
                      width: '250px',
                      height: '350px',
                    }}
                    whileHover={{ scale: 1.05 }}
                  >
                    <div className="w-full h-full overflow-hidden rounded-2xl shadow-2xl">
                      <img
                        src={img}
                        alt={`Food showcase ${index + 1}`}
                        className="w-full h-full object-cover"
                      />
                      <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent"></div>
                      <div className="absolute bottom-0 w-full p-4 text-white">
                        <div className="flex items-center justify-between">
                          <div className="text-sm font-bold bg-primary/80 px-2 py-0.5 rounded">
                            â‚¹{120 + (index * 10)}
                          </div>
                          <div className="flex items-center">
                            <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 text-yellow-400 fill-current" viewBox="0 0 20 20">
                              <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                            </svg>
                            <span className="text-xs ml-1">4.8</span>
                          </div>
                        </div>
                      </div>
                    </div>
                  </motion.div>
                );
              })}
            </div>

            {/* Carousel Controls - Hidden on mobile */}
            <div className="carousel-controls hidden sm:flex">
              <button 
                className="carousel-control-button"
                onClick={() => {
                  const carousel = carouselRef.current;
                  if (carousel) {
                    carousel.style.animationPlayState = 'paused';
                    setTimeout(() => {
                      carousel.style.animationPlayState = 'running';
                    }, 1000);
                  }
                }}
              >
                <FontAwesomeIcon icon={faChevronLeft} />
              </button>
              <button 
                className="carousel-control-button"
                onClick={() => {
                  const carousel = carouselRef.current;
                  if (carousel) {
                    carousel.style.animationPlayState = 'paused';
                    setTimeout(() => {
                      carousel.style.animationPlayState = 'running';
                    }, 1000);
                  }
                }}
              >
                <FontAwesomeIcon icon={faChevronRight} />
              </button>
            </div>

            {/* Speed Indicator - Hidden on mobile */}
            <div className="carousel-speed-indicator hidden sm:block">
              <span>Scroll to spin faster</span>
              <div className="speed-progress">
                <div 
                  className="speed-progress-bar" 
                  style={{ width: `${(rotationSpeed / 3) * 100}%` }}
                ></div>
              </div>
              <span>{rotationSpeed.toFixed(1)}x</span>
            </div>
          </div>
        </div>
      </div>

      {/* Scroll indicator */}
      <motion.div 
        className="absolute bottom-8 left-1/2 transform -translate-x-1/2 flex flex-col items-center text-gray-400"
        animate={{ 
          y: [0, 10, 0],
          opacity: [0.6, 1, 0.6]
        }}
        transition={{ 
          duration: 2,
          ease: "easeInOut",
          repeat: Infinity
        }}
      >
        <div className="text-gray-500 mb-2 text-sm tracking-wide">Scroll to discover</div>
        <FontAwesomeIcon icon={faAngleDown} className="w-6 h-6" />
      </motion.div>
    </section>
  );
};

export default HeroSection;
