        }
      } else {
        // Remove padding when not sticky
        document.body.style.paddingTop = '';
      }

      // Determine active cell based on scroll position
      let activeCell = 0;
      if (cellsSticky) {
        const scrollProgress = (scrollY - mainHeaderBottom) / (lastCellBottom - mainHeaderBottom - windowHeight);
        if (scrollProgress > 0.25) activeCell = 1;
        if (scrollProgress > 0.5) activeCell = 2;
        if (scrollProgress > 0.75) activeCell = 3;
      }

      setStickyState({
        headerSticky,
        cellsSticky,
        activeCell
      });
    };

