import React from 'react';
import { motion } from 'framer-motion';
import './ProblemStatement.css';

const ProblemStatement = () => {
  const problems = [
    {
      id: 'busy',
      title: 'Too Busy to Cook',
      description: 'Your schedule is packed. Between work, commute, and life, who has time to plan, shop, and cook?',
      icon: '‚è∞',
      fact: 'Average person works 8+ hours daily',
      color: '#FF6B6B'
    },
    {
      id: 'junk',
      title: 'Stuck with Junk Food',
      description: 'Quick fixes lead to unhealthy choices. Fast food becomes the norm when convenience matters most.',
      icon: 'üçî',
      fact: '70% of daily intake is processed food',
      color: '#4ECDC4'
    },
    {
      id: 'expensive',
      title: 'Expensive & Wasteful',
      description: 'Ordering out daily burns through your budget. Groceries expire unused. Money down the drain.',
      icon: 'üí∏',
      fact: 'Spend ‚Çπ500+ daily on food delivery',
      color: '#45B7D1'
    },
    {
      id: 'away',
      title: 'Away from Home',
      description: 'Living away from family means no home-cooked meals. Missing that comfort and nutrition.',
      icon: 'üè†',
      fact: 'Missing the comfort of home-cooked meals',
      color: '#96CEB4'
    }
  ];

  return (
    <section id="problem-statement" className="problem-section">
      <div className="container mx-auto px-4 md:px-6">
        {/* Header */}
        <motion.div 
          className="text-center mb-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          <motion.h2 
            className="problem-title"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            We Know Your <span className="text-red-600">Daily Struggle</span>
          </motion.h2>
          <motion.p 
            className="problem-subtitle"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            Modern life shouldn't mean compromising on nutrition and taste
          </motion.p>
        </motion.div>

        {/* Problems Grid */}
        <div className="problems-grid">
          {problems.map((problem, index) => (
            <motion.div
              key={problem.id}
              className="problem-card"
              initial={{ opacity: 0, y: 40 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.6, delay: index * 0.15 }}
              whileHover={{ y: -8, transition: { duration: 0.3 } }}
            >
              <div className="problem-card-inner">
                <motion.div 
                  className="problem-icon-container"
                  style={{ backgroundColor: `${problem.color}15` }}
                  whileHover={{ scale: 1.1, rotate: 5 }}
                  transition={{ duration: 0.3 }}
                >
                  <span className="problem-icon" style={{ color: problem.color }}>
                    {problem.icon}
                  </span>
                </motion.div>

                <div className="problem-content">
                  <h3 className="problem-card-title">{problem.title}</h3>
                  <p className="problem-card-description">{problem.description}</p>

                  <motion.div 
                    className="problem-stat"
                    style={{ borderColor: `${problem.color}40`, color: problem.color }}
                    initial={{ opacity: 0, scale: 0.8 }}
                    whileInView={{ opacity: 1, scale: 1 }}
                    viewport={{ once: true }}
                    transition={{ duration: 0.5, delay: 0.6 + index * 0.1 }}
                  >
                    {problem.fact}
                  </motion.div>
                </div>
              </div>
            </motion.div>
          ))}
        </div>

        {/* Solution Preview */}
        <motion.div 
          className="solution-preview"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8, delay: 0.8 }}
        >
          <div className="solution-content">
            <h3 className="solution-title">Ready to Break Free from These Problems?</h3>
            <p className="solution-description">
              Let us handle your daily meals while you focus on what matters most. 
              Fresh, nutritious, and delicious food delivered right to your doorstep.
            </p>
            <motion.button 
              className="solution-button"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => {
                const section = document.getElementById('subscription-plans');
                if (section) {
                  window.scrollTo({
                    top: section.offsetTop - 80,
                    behavior: 'smooth'
                  });
                }
              }}
            >
              <span>See Our Solution</span>
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M5 12H19M19 12L12 5M19 12L12 19" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </motion.button>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default ProblemStatement;
