import React, { useState, useEffect, useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';

const SubscriptionPlans = () => {
  // State to track selected meal combinations for each plan
  const [selectedMeals, setSelectedMeals] = useState({
    'one-meal': 'lunch', // Default: lunch only
    'two-meals': 'lunch-dinner', // Default: lunch + dinner
    'three-meals': 'all' // Default: all meals
  });

  const containerRef = useRef(null);
  const { scrollYProgress } = useScroll({
    target: containerRef,
    offset: ["start start", "end start"]
  });

  // Meal pricing configurations
  const mealPricing = {
    'one-meal': {
      'lunch': { price: 120, description: 'Lunch Only', meals: ['lunch'] },
      'dinner': { price: 120, description: 'Dinner Only', meals: ['dinner'] },
      'breakfast': { price: 80, description: 'Breakfast Only', meals: ['breakfast'] }
    },
    'two-meals': {
      'lunch-dinner': { price: 220, description: 'Lunch + Dinner', meals: ['lunch', 'dinner'] },
      'lunch-breakfast': { price: 200, description: 'Lunch + Breakfast', meals: ['breakfast', 'lunch'] },
      'dinner-breakfast': { price: 200, description: 'Dinner + Breakfast', meals: ['breakfast', 'dinner'] }
    },
    'three-meals': {
      'all': { price: 250, description: 'All Meals', meals: ['breakfast', 'lunch', 'dinner'] }
    }
  };

  const pricingPlans = [
    {
      id: 'one-meal',
      title: 'Single',
      description: 'One meal daily',
      popular: false,
      accent: '#8B4513', // Brown
      bgGradient: 'from-amber-50 to-orange-100'
    },
    {
      id: 'two-meals',
      title: 'Double',
      description: 'Two meals daily',
      popular: true,
      accent: '#DC2626', // Red
      bgGradient: 'from-red-50 to-pink-100'
    },
    {
      id: 'three-meals',
      title: 'Triple',
      description: 'Three meals daily',
      popular: false,
      accent: '#1F2937', // Dark gray/black
      bgGradient: 'from-slate-50 to-gray-100'
    }
  ];

  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      window.scrollTo({
        top: section.offsetTop - 80,
        behavior: 'smooth'
      });
    }
  };

  const formatPrice = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0
    }).format(amount);
  };

  const renderMealIcon = (planId, mealKey, config, accentColor, isSelected) => {
    const { meals } = config;

    return (
      <motion.button
        onClick={() => setSelectedMeals(prev => ({ ...prev, [planId]: mealKey }))}
        className={`relative p-2 border-2 transition-all rounded-lg ${
          isSelected ? 'border-gray-800 bg-white shadow-md' : 'border-gray-300 hover:border-gray-500 bg-white/70'
        }`}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
      >
        {/* Meal grid (3 rows x 1 column) */}
        <div className="w-12 h-16 grid grid-rows-3 gap-0.5">
          {/* Breakfast */}
          <div className={`w-full h-full border border-gray-400 rounded-sm ${
            meals.includes('breakfast') ? 'bg-orange-400' : 'bg-gray-200'
          }`}>
            <div className="text-xs text-center leading-4 text-gray-700">üåÖ</div>
          </div>

          {/* Lunch */}
          <div className={`w-full h-full border border-gray-400 rounded-sm ${
            meals.includes('lunch') ? 'bg-yellow-400' : 'bg-gray-200'
          }`}>
            <div className="text-xs text-center leading-4 text-gray-700">‚òÄÔ∏è</div>
          </div>

          {/* Dinner */}
          <div className={`w-full h-full border border-gray-400 rounded-sm ${
            meals.includes('dinner') ? 'bg-blue-400' : 'bg-gray-200'
          }`}>
            <div className="text-xs text-center leading-4 text-gray-700">üåô</div>
          </div>
        </div>
      </motion.button>
    );
  };

  const renderMealSelectors = (planId, accentColor) => {
    const planOptions = mealPricing[planId];

    return (
      <div className="flex justify-center items-center gap-3 mb-8">
        {Object.entries(planOptions).map(([mealKey, config]) => (
          <div key={mealKey}>
            {renderMealIcon(
              planId, 
              mealKey, 
              config, 
              accentColor, 
              selectedMeals[planId] === mealKey
            )}
          </div>
        ))}
      </div>
    );
  };

  // Transform values for stacking effect
  const card1Y = useTransform(scrollYProgress, [0, 0.33], [0, -100]);
  const card1Rotate = useTransform(scrollYProgress, [0, 0.33], [0, -12]);
  const card1Scale = useTransform(scrollYProgress, [0, 0.33], [1, 0.9]);

  const card2Y = useTransform(scrollYProgress, [0.33, 0.66], [0, -100]);
  const card2Rotate = useTransform(scrollYProgress, [0.33, 0.66], [0, -12]);
  const card2Scale = useTransform(scrollYProgress, [0.33, 0.66], [1, 0.9]);

  const card3Y = useTransform(scrollYProgress, [0.66, 1], [0, -100]);
  const card3Rotate = useTransform(scrollYProgress, [0.66, 1], [0, -12]);
  const card3Scale = useTransform(scrollYProgress, [0.66, 1], [1, 0.9]);

  return (
    <section id="subscription-plans" className="relative overflow-hidden" style={{backgroundColor: '#f9f5f0'}}>
      {/* Section Header - Fixed */}
      <div className="relative z-20 pt-16 pb-8">
        <div className="container mx-auto px-4 md:px-6">
          <motion.div 
            className="text-center"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8 }}
          >
            <motion.h2 
              className="text-4xl md:text-5xl font-bold text-gray-900 mb-4"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              Built for your budget
            </motion.h2>
            <motion.p 
              className="text-lg text-gray-600 max-w-2xl mx-auto"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.8, delay: 0.4 }}
            >
              Choose the perfect plan that fits your lifestyle and budget. All plans include free delivery and premium quality meals.
            </motion.p>
          </motion.div>
        </div>
      </div>

      {/* Stacking Cards Container */}
      <div ref={containerRef} className="relative" style={{ height: '400vh' }}>
        <div className="sticky top-0 h-screen flex items-center justify-center px-4 md:px-6">
          <div className="relative w-full max-w-md mx-auto">

            {/* Card 3 - Bottom (Triple) */}
            <motion.div
              style={{ 
                y: card3Y,
                rotate: card3Rotate,
                scale: card3Scale,
                zIndex: 1
              }}
              className="absolute inset-0"
            >
              {(() => {
                const plan = pricingPlans[2];
                const currentConfig = mealPricing[plan.id][selectedMeals[plan.id]];

                return (
                  <div className={`bg-gradient-to-br ${plan.bgGradient} backdrop-blur-sm border-2 border-gray-900 shadow-2xl rounded-2xl overflow-hidden h-96 w-full`}>
                    {/* Accent line */}
                    <div 
                      className="h-2 w-full" 
                      style={{backgroundColor: plan.accent}}
                    ></div>

                    {/* Header */}
                    <div className="p-6 text-center">
                      <h3 className="text-2xl font-black text-gray-900 mb-2 tracking-tight">{plan.title}</h3>
                      <p className="text-gray-600 text-xs uppercase tracking-widest mb-4">{currentConfig.description}</p>

                      {/* Meal selectors */}
                      {renderMealSelectors(plan.id, plan.accent)}

                      {/* Price */}
                      <div className="text-center mb-4">
                        <div className="text-3xl font-black text-gray-900 mb-1">
                          {formatPrice(currentConfig.price)}
                        </div>
                        <div className="text-xs text-gray-500 uppercase tracking-widest">per day</div>
                      </div>

                      {/* Features */}
                      <div className="space-y-2 mb-4 text-sm">
                        <div className="flex items-center justify-between">
                          <span className="text-gray-700 font-medium">All Meals</span>
                          <span className="text-gray-600">Premium</span>
                        </div>
                      </div>

                      {/* CTA Button */}
                      <motion.button
                        onClick={() => scrollToSection('daily-menu')}
                        className="w-full py-3 font-bold text-sm transition-all border-2 border-gray-900 rounded-lg hover:bg-gray-900 hover:text-white"
                        style={{
                          backgroundColor: plan.popular ? plan.accent : 'white',
                          color: plan.popular ? 'white' : '#1F2937'
                        }}
                        whileHover={{ scale: 1.02 }}
                        whileTap={{ scale: 0.98 }}
                      >
                        Choose Plan
                      </motion.button>
                    </div>
                  </div>
                );
              })()}
            </motion.div>

            {/* Card 2 - Middle (Double) */}
            <motion.div
              style={{ 
                y: card2Y,
                rotate: card2Rotate,
                scale: card2Scale,
                zIndex: 2
              }}
              className="absolute inset-0"
            >
              {(() => {
                const plan = pricingPlans[1];
                const currentConfig = mealPricing[plan.id][selectedMeals[plan.id]];

                return (
                  <div className={`bg-gradient-to-br ${plan.bgGradient} backdrop-blur-sm border-2 border-gray-900 shadow-2xl rounded-2xl overflow-hidden h-96 w-full`}>
                    {/* Popular badge */}
                    {plan.popular && (
                      <div className="absolute -top-2 left-1/2 transform -translate-x-1/2 z-20">
                        <div className="bg-red-600 text-white text-xs font-bold px-4 py-1 tracking-wider rounded-full">
                          POPULAR
                        </div>
                      </div>
                    )}

                    {/* Accent line */}
                    <div 
                      className="h-2 w-full" 
                      style={{backgroundColor: plan.accent}}
                    ></div>

                    {/* Header */}
                    <div className="p-6 text-center">
                      <h3 className="text-2xl font-black text-gray-900 mb-2 tracking-tight">{plan.title}</h3>
                      <p className="text-gray-600 text-xs uppercase tracking-widest mb-4">{currentConfig.description}</p>

                      {/* Meal selectors */}
                      {renderMealSelectors(plan.id, plan.accent)}

                      {/* Price */}
                      <div className="text-center mb-4">
                        <div className="text-3xl font-black text-gray-900 mb-1">
                          {formatPrice(currentConfig.price)}
                        </div>
                        <div className="text-xs text-gray-500 uppercase tracking-widest">per day</div>
                      </div>

                      {/* Features */}
                      <div className="space-y-2 mb-4 text-sm">
                        <div className="flex items-center justify-between">
                          <span className="text-gray-700 font-medium">Lunch + Dinner</span>
                          <span className="text-gray-600">Premium</span>
                        </div>
                      </div>

                      {/* CTA Button */}
                      <motion.button
                        onClick={() => scrollToSection('daily-menu')}
                        className="w-full py-3 font-bold text-sm transition-all border-2 border-gray-900 rounded-lg hover:bg-gray-900 hover:text-white"
                        style={{
                          backgroundColor: plan.popular ? plan.accent : 'white',
                          color: plan.popular ? 'white' : '#1F2937'
                        }}
                        whileHover={{ scale: 1.02 }}
                        whileTap={{ scale: 0.98 }}
                      >
                        Choose Plan
                      </motion.button>
                    </div>
                  </div>
                );
              })()}
            </motion.div>

            {/* Card 1 - Top (Single) */}
            <motion.div
              style={{ 
                y: card1Y,
                rotate: card1Rotate,
                scale: card1Scale,
                zIndex: 3
              }}
              className="absolute inset-0"
            >
              {(() => {
                const plan = pricingPlans[0];
                const currentConfig = mealPricing[plan.id][selectedMeals[plan.id]];

                return (
                  <div className={`bg-gradient-to-br ${plan.bgGradient} backdrop-blur-sm border-2 border-gray-900 shadow-2xl rounded-2xl overflow-hidden h-96 w-full`}>
                    {/* Accent line */}
                    <div 
                      className="h-2 w-full" 
                      style={{backgroundColor: plan.accent}}
                    ></div>

                    {/* Header */}
                    <div className="p-6 text-center">
                      <h3 className="text-2xl font-black text-gray-900 mb-2 tracking-tight">{plan.title}</h3>
                      <p className="text-gray-600 text-xs uppercase tracking-widest mb-4">{currentConfig.description}</p>

                      {/* Meal selectors */}
                      {renderMealSelectors(plan.id, plan.accent)}

                      {/* Price */}
                      <div className="text-center mb-4">
                        <div className="text-3xl font-black text-gray-900 mb-1">
                          {formatPrice(currentConfig.price)}
                        </div>
                        <div className="text-xs text-gray-500 uppercase tracking-widest">per day</div>
                      </div>

                      {/* Features */}
                      <div className="space-y-2 mb-4 text-sm">
                        <div className="flex items-center justify-between">
                          <span className="text-gray-700 font-medium">Lunch Only</span>
                          <span className="text-gray-600">Premium</span>
                        </div>
                      </div>

                      {/* CTA Button */}
                      <motion.button
                        onClick={() => scrollToSection('daily-menu')}
                        className="w-full py-3 font-bold text-sm transition-all border-2 border-gray-900 rounded-lg hover:bg-gray-900 hover:text-white"
                        style={{
                          backgroundColor: plan.popular ? plan.accent : 'white',
                          color: plan.popular ? 'white' : '#1F2937'
                        }}
                        whileHover={{ scale: 1.02 }}
                        whileTap={{ scale: 0.98 }}
                      >
                        Choose Plan
                      </motion.button>
                    </div>
                  </div>
                );
              })()}
            </motion.div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default SubscriptionPlans;
