import React, { useRef, useState, useEffect } from "react";
import { createPortal } from "react-dom";
import { motion, useInView } from "framer-motion";

export default function HowItWorks() {
  const containerRef = useRef(null);
  const nonStickyHeaderRef = useRef(null); // Renamed for clarity
  const isInView = useInView(containerRef, { once: true, amount: 0.2 });

  // State for sticky header effect
  const [isSticky, setIsSticky] = useState(false);
  const [stickyTop, setStickyTop] = useState(0); // Default sticky top position - CHANGED
  const [headerHeight, setHeaderHeight] = useState(0); // To store the height of the non-sticky header

  useEffect(() => {
    // Measure the height of the non-sticky header when it's rendered
    if (!isSticky && nonStickyHeaderRef.current) {
      setHeaderHeight(nonStickyHeaderRef.current.offsetHeight);
    }
  }, [isSticky, nonStickyHeaderRef.current]); // Re-run if isSticky changes or ref updates

  useEffect(() => {
    const handleScroll = () => {
      const originalHeaderElement = nonStickyHeaderRef.current;
      const howItWorksSection = document.getElementById('how-it-works'); // Get the How It Works section itself
      const testimonialsSection = document.getElementById('testimonials');
      const pricingSection = document.getElementById('subscription-plans'); // Keep this for now, might be needed or adjusted

      if (!originalHeaderElement || !howItWorksSection || !testimonialsSection || !pricingSection) {
        return;
      }

      const originalHeaderRect = originalHeaderElement.getBoundingClientRect();
      const howItWorksRect = howItWorksSection.getBoundingClientRect(); // Get bounds for the How It Works section
      const testimonialsRect = testimonialsSection.getBoundingClientRect();
      const pricingRect = pricingSection.getBoundingClientRect(); // Keep this for now
      const viewportHeight = window.innerHeight;

      // Activation: when original header scrolls past top of viewport
      const shouldActivate = originalHeaderRect.top <= 0;

      // Deactivation:
      // 1. Scrolling Down: When the top of the testimonials section touches or goes above the bottom of the viewport.
      const deactivateScrollingDown = testimonialsRect.top <= viewportHeight;

      // 2. Scrolling Up: When the bottom of the 'How It Works' section itself goes above the top of the viewport.
      const deactivateScrollingUp = howItWorksRect.bottom <= 0;

      // Determine if it should be sticky
      // It should be sticky if it's activated AND NOT meeting deactivation conditions.
      // Deactivation happens if we are scrolling down past testimonials OR scrolling up past the HowItWorks section.
      // We need to be careful here. If originalHeaderRect.top > 0, it should definitely not be sticky.

      let shouldBeSticky = false;
      if (originalHeaderRect.top <= 0) { // Primary condition: header is at or above viewport top
        if (deactivateScrollingDown && originalHeaderRect.bottom < testimonialsRect.top) { 
          // If scrolling down and testimonials is visible, but HowItWorks header is still above testimonials
          shouldBeSticky = true;
        } else if (deactivateScrollingDown) {
           shouldBeSticky = false;
        } else if (deactivateScrollingUp) {
           shouldBeSticky = false;
        }
         else {
          shouldBeSticky = true; // Stick if activated and no deactivation condition met
        }
      } else {
        shouldBeSticky = false; // Not sticky if original header is below viewport top
      }


      // Correction: The previous logic for shouldBeSticky was a bit tangled. Let's simplify.
      // Activate when originalHeaderRect.top <= 0
      // Deactivate if:
      //    - (Scrolling Down) testimonialsRect.top <= stickyTop (Testimonials section top is at or above sticky header top)
      //    - (Scrolling Up) AND howItWorksRect.bottom <= 0 (HowItWorks section bottom is at or above viewport top)
      //    - OR if originalHeaderRect.top > 0 (the non-sticky header is visible again)

      if (originalHeaderRect.top > 0) { // Non-sticky header is visible
        setIsSticky(false);
      } else {
        // Header is at or past the top, now check deactivation
        const isScrollingDownPastTestimonials = testimonialsRect.top <= stickyTop;
        const isScrollingUpPastHowItWorks = howItWorksRect.bottom <= 0;

        if (isScrollingDownPastTestimonials || isScrollingUpPastHowItWorks) {
          setIsSticky(false);
        } else {
          setIsSticky(true);
        }
      }
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    window.addEventListener('resize', handleScroll);

    // Initial check
    handleScroll();

    return () => {
      window.removeEventListener('scroll', handleScroll);
      window.removeEventListener('resize', handleScroll);
    };
  }, []);

  const handleWhatsAppContact = () => {
    // Replace with your actual WhatsApp number
    const phoneNumber = '+918123456789'; // Update this with your actual WhatsApp number
    const message = encodeURIComponent('Hi! I\'m interested in Myna Kitchen\'s meal plans and would like to discuss my requirements to choose the best plan for me.');
    const whatsappUrl = `https://wa.me/${phoneNumber}?text=${message}`;

    window.open(whatsappUrl, '_blank');
  };

  const steps = [
    {
      number: "01",
      title: "Browse & Choose",
      description: "Explore our delicious meal plans and find the perfect fit for your lifestyle and dietary needs.",
      accent: "#8B4513",
      bgGradient: "bg-gradient-to-br from-orange-50 to-yellow-50",
      illustration: (
        <motion.div 
          className="w-16 h-16 mx-auto flex items-center justify-center"
          whileHover={{ scale: 1.05 }}
          transition={{ duration: 0.3 }}
        >
          <div className="grid grid-cols-2 gap-1 w-14 h-14">
            <div className="bg-gradient-to-br from-orange-200 to-orange-300 border border-orange-400 rounded-lg flex items-center justify-center shadow-sm">
              <span className="text-sm">ü•ó</span>
            </div>
            <div className="bg-gradient-to-br from-green-200 to-green-300 border border-green-400 rounded-lg flex items-center justify-center shadow-sm">
              <span className="text-sm">üçõ</span>
            </div>
            <div className="bg-gradient-to-br from-blue-200 to-blue-300 border border-blue-400 rounded-lg flex items-center justify-center shadow-sm">
              <span className="text-sm">üçú</span>
            </div>
            <div className="bg-gradient-to-br from-yellow-200 to-yellow-300 border border-yellow-400 rounded-lg flex items-center justify-center shadow-sm">
              <span className="text-sm">ü•ò</span>
            </div>
          </div>
        </motion.div>
      )
    },
    {
      number: "02", 
      title: "Connect on WhatsApp",
      description: "Get in touch with our team to discuss your requirements and get personalized meal recommendations.",
      accent: "#059669",
      bgGradient: "bg-gradient-to-br from-green-50 to-emerald-50",
      illustration: (
        <motion.div 
          className="w-16 h-16 mx-auto flex items-center justify-center"
          whileHover={{ scale: 1.05 }}
          transition={{ duration: 0.3 }}
        >
          <div className="w-14 h-14 bg-gradient-to-br from-green-100 to-green-200 border-2 border-green-400 rounded-2xl flex items-center justify-center shadow-lg">
            <div className="relative">
              <motion.div 
                className="w-8 h-8 bg-gradient-to-br from-green-500 to-green-600 rounded-full flex items-center justify-center shadow-md"
                animate={{ scale: [1, 1.05, 1] }}
                transition={{ duration: 2, repeat: Infinity }}
              >
                <span className="text-white text-xs font-bold">üí¨</span>
              </motion.div>
              <motion.div 
                className="absolute -top-1 -right-1 w-3 h-3 bg-green-600 rounded-full"
                animate={{ opacity: [0.7, 1, 0.7] }}
                transition={{ duration: 1.5, repeat: Infinity }}
              />
            </div>
          </div>
        </motion.div>
      ),
      hasButton: true
    },
    {
      number: "03",
      title: "Sign Up & Select",
      description: "Complete your registration and choose your preferred plan with flexible scheduling options.",
      accent: "#7C3AED",
      bgGradient: "bg-gradient-to-br from-purple-50 to-violet-50",
      illustration: (
        <motion.div 
          className="w-16 h-16 mx-auto flex items-center justify-center"
          whileHover={{ scale: 1.05 }}
          transition={{ duration: 0.3 }}
        >
          <div className="bg-gradient-to-br from-purple-100 to-purple-200 border-2 border-purple-400 rounded-xl w-14 h-14 flex flex-col justify-center items-center shadow-lg">
            <motion.div 
              className="w-8 h-1 bg-gradient-to-r from-purple-400 to-purple-500 rounded mb-1"
              initial={{ width: 0 }}
              animate={isInView ? { width: 32 } : { width: 0 }}
              transition={{ duration: 0.8, delay: 0.5 }}
            />
            <motion.div 
              className="w-6 h-1 bg-gradient-to-r from-purple-300 to-purple-400 rounded mb-1"
              initial={{ width: 0 }}
              animate={isInView ? { width: 24 } : { width: 0 }}
              transition={{ duration: 0.8, delay: 0.7 }}
            />
            <motion.div 
              className="w-4 h-4 bg-gradient-to-br from-purple-500 to-purple-600 rounded flex items-center justify-center shadow-sm"
              initial={{ scale: 0 }}
              animate={isInView ? { scale: 1 } : { scale: 0 }}
              transition={{ duration: 0.5, delay: 1 }}
            >
              <span className="text-white text-xs">‚úì</span>
            </motion.div>
          </div>
        </motion.div>
      )
    },
    {
      number: "04",
      title: "Enjoy Daily Deliveries",
      description: "Start your subscription and enjoy fresh, tasty meals delivered to your doorstep every day.",
      accent: "#DC2626",
      bgGradient: "bg-gradient-to-br from-red-50 to-pink-50",
      illustration: (
        <motion.div 
          className="w-16 h-16 mx-auto flex items-center justify-center"
          whileHover={{ scale: 1.05 }}
          transition={{ duration: 0.3 }}
        >
          <div className="relative bg-gradient-to-br from-red-100 to-red-200 border-2 border-red-400 rounded-2xl w-14 h-14 flex items-center justify-center shadow-lg">
            <motion.div 
              className="w-8 h-8 bg-gradient-to-br from-red-500 to-red-600 rounded-full flex items-center justify-center shadow-md"
              animate={{ x: [0, 1, 0] }}
              transition={{ duration: 2, repeat: Infinity }}
            >
              <span className="text-white text-xs">üöö</span>
            </motion.div>
            <motion.div 
              className="absolute top-2 right-2 w-2 h-2 bg-red-600 rounded-full"
              animate={{ scale: [1, 1.1, 1] }}
              transition={{ duration: 1.5, repeat: Infinity }}
            />
            <div className="absolute bottom-2 left-2 w-1 h-1 bg-red-400 rounded-full" />
          </div>
        </motion.div>
      )
    }
  ];

  return (
    <section id="how-it-works" className="pt-8 pb-20 md:pb-28 overflow-hidden section-fade relative" style={{backgroundColor: '#f9f5f0'}}>
      {/* Subtle background decoration */}
      <div className="absolute inset-0 opacity-[0.02]">
        <div className="absolute top-20 left-10 w-32 h-32 bg-gray-900 rounded-full blur-3xl" />
        <div className="absolute bottom-20 right-10 w-40 h-40 bg-gray-900 rounded-full blur-3xl" />
      </div>

      <div className="container mx-auto px-4 md:px-6 relative">
        {/* Spacer for sticky header, uses measured height of non-sticky header */}
        {isSticky && headerHeight > 0 && <div style={{ height: `${headerHeight}px` }} />}

        {/* Section Header - Renders non-sticky version in place */}
        {!isSticky && (
          <motion.div 
            ref={nonStickyHeaderRef} // Ref to measure height for the spacer
            className="text-center mb-20"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8 }}
          >
            {/* This container is important for non-sticky layout */}
            <div className="container mx-auto px-4 md:px-6">
              <motion.div
                className="inline-flex items-center bg-gradient-to-r from-gray-100 to-gray-50 text-gray-700 px-6 py-3 rounded-full mb-8 font-semibold text-sm border border-gray-200 shadow-sm"
                initial={{ opacity: 0, scale: 0.9 }}
                whileInView={{ opacity: 1, scale: 1 }}
                viewport={{ once: true }}
                transition={{ duration: 0.6, delay: 0.1 }}
              >
                <span className="w-2 h-2 bg-gray-400 rounded-full mr-3 animate-pulse" />
                Simple Process
              </motion.div>

              <motion.h2 
                className="text-4xl md:text-5xl lg:text-6xl font-black text-gray-900 mb-6 tracking-tight"
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.8, delay: 0.2 }}
              >
                How It Works
              </motion.h2>
              <motion.p 
                className="text-lg md:text-xl text-gray-600 max-w-3xl mx-auto leading-relaxed mb-0"
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.8, delay: 0.4 }}
              >
                From browsing to enjoying delicious meals at your doorstep - we've made it incredibly simple in just 4 easy steps.
              </motion.p>
            </div>
          </motion.div>
        )}

        {/* Sticky Header - Rendered into document.body using a Portal */}
        {isSticky && typeof document !== 'undefined' && createPortal(
          <div 
            className="fixed left-0 right-0 z-40 bg-[#f9f5f0] shadow-xl border-b-2 border-gray-300 py-6 text-center"
            style={{ 
              top: `${stickyTop}px`,
              width: '100%'
            }}
          >
            <div className="container mx-auto px-4 md:px-6">
              <div
                className="inline-flex items-center bg-gradient-to-r from-gray-100 to-gray-50 text-gray-700 px-6 py-3 rounded-full mb-8 font-semibold text-sm border border-gray-200 shadow-sm"
              >
                <span className="w-2 h-2 bg-gray-400 rounded-full mr-3 animate-pulse" />
                Simple Process
              </div>

              <h2 
                className="text-2xl md:text-3xl lg:text-4xl font-black text-gray-900 mb-6 tracking-tight"
              >
                How It Works
              </h2>
              <p 
                className="text-sm md:text-base text-gray-600 max-w-3xl mx-auto leading-relaxed mb-2"
              >
                From browsing to enjoying delicious meals at your doorstep - we've made it incredibly simple in just 4 easy steps.
              </p>
            </div>
          </div>,
          document.body
        )}

        <div ref={containerRef} className="max-w-7xl mx-auto grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {steps.map((step, index) => (
            <motion.div
              key={index}
              className="relative flex group"
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.8, delay: index * 0.2 }}
              whileHover={{ y: -6, transition: { duration: 0.3 } }}
            >
              {/* Main card */}
              <div className={`${step.bgGradient} border-2 border-gray-900 shadow-2xl overflow-hidden relative w-full flex flex-col rounded-lg group-hover:shadow-3xl transition-all duration-500`}>
                {/* Accent line */}
                <div 
                  className="h-2 w-full flex-shrink-0" 
                  style={{backgroundColor: step.accent}}
                />

                {/* Header */}
                <div className="p-8 text-center border-b border-gray-200/50 flex-shrink-0 min-h-[160px] flex flex-col justify-center">
                  {/* Step number */}
                  <motion.div 
                    className="w-14 h-14 bg-gray-900 text-white rounded-full flex items-center justify-center font-black text-xl mx-auto mb-6 shadow-lg group-hover:scale-105 transition-transform duration-300"
                    whileHover={{ rotate: 3 }}
                  >
                    {step.number}
                  </motion.div>

                  {/* Title */}
                  <div className="h-16 flex items-center justify-center">
                    <h3 className="text-xl md:text-2xl font-black text-gray-900 tracking-tight leading-tight text-center group-hover:text-gray-800 transition-colors duration-300">
                      {step.title}
                    </h3>
                  </div>
                </div>

                {/* Content area */}
                <div className="p-8 flex flex-col flex-grow">
                  {/* Visual illustration */}
                  <div className="mb-8 flex-shrink-0">
                    {step.illustration}
                  </div>

                  {/* Description and actions */}
                  <div className="flex-grow flex flex-col justify-between">
                    <p className="text-gray-700 text-sm md:text-base leading-relaxed mb-8 group-hover:text-gray-800 transition-colors duration-300">
                      {step.description}
                    </p>

                    {/* WhatsApp Button */}
                    {step.hasButton && (
                      <div className="mb-8 flex-shrink-0">
                        <motion.button
                          onClick={handleWhatsAppContact}
                          className="w-full py-4 bg-gradient-to-r from-green-600 to-green-700 hover:from-green-700 hover:to-green-800 text-white font-bold text-sm border-2 border-green-600 hover:border-green-700 transition-all duration-300 flex items-center justify-center gap-3 rounded-lg shadow-lg hover:shadow-xl"
                          whileHover={{ scale: 1.02, y: -1 }}
                          whileTap={{ scale: 0.98 }}
                        >
                          <motion.span 
                            className="text-lg"
                            animate={{ scale: [1, 1.1, 1] }}
                            transition={{ duration: 2, repeat: Infinity }}
                          >
                            üí¨
                          </motion.span>
                          Contact on WhatsApp
                        </motion.button>
                      </div>
                    )}

                    {/* Feature highlight */}
                    <div className="border-t border-gray-200/50 pt-6 flex-shrink-0">
                      <div className="flex items-center justify-center">
                        <motion.div 
                          className="w-3 h-3 rounded-full mr-3 flex-shrink-0 shadow-sm" 
                          style={{backgroundColor: step.accent}}
                          animate={{ scale: [1, 1.05, 1] }}
                          transition={{ duration: 2, repeat: Infinity, delay: index * 0.3 }}
                        />
                        <span className="text-xs md:text-sm text-gray-600 uppercase tracking-widest font-bold text-center group-hover:text-gray-700 transition-colors duration-300">
                          {index === 0 && "Multiple Options"}
                          {index === 1 && "Instant Support"}
                          {index === 2 && "Easy Setup"}
                          {index === 3 && "Fresh Daily"}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Hover overlay */}
                <div className="absolute inset-0 bg-white/5 opacity-0 group-hover:opacity-100 transition-opacity duration-500 pointer-events-none rounded-lg" />
              </div>

              {/* Connecting arrows */}
              {index < steps.length - 1 && (
                <div className="hidden lg:block absolute top-1/2 -right-6 transform -translate-y-1/2 z-10">
                  <motion.div
                    initial={{ opacity: 0, x: -15, scale: 0.9 }}
                    animate={isInView ? { opacity: 1, x: 0, scale: 1 } : { opacity: 0, x: -15, scale: 0.9 }}
                    transition={{ duration: 0.8, delay: index * 0.2 + 0.5 }}
                    className="w-12 h-12 bg-white border-2 border-gray-900 rounded-full flex items-center justify-center shadow-xl hover:shadow-2xl transition-shadow duration-300"
                    whileHover={{ scale: 1.05, rotate: 5 }}
                  >
                    <motion.span 
                      className="text-gray-900 font-black text-lg"
                      animate={{ x: [0, 1, 0] }}
                      transition={{ duration: 2, repeat: Infinity }}
                    >
                      ‚Üí
                    </motion.span>
                  </motion.div>
                </div>
              )}
            </motion.div>
          ))}
        </div>

        {/* Call to Action */}
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 50 }}
          transition={{ duration: 1, delay: 1.2 }}
          className="text-center mt-20"
          id="ready-to-get-started" // ADDED ID
        >
          <motion.div 
            className="bg-gradient-to-br from-white to-gray-50 border-2 border-gray-900 shadow-2xl p-10 max-w-2xl mx-auto rounded-2xl hover:shadow-3xl transition-all duration-500"
            whileHover={{ y: -3, scale: 1.01 }}
          >
            <h3 className="text-3xl font-black text-gray-900 mb-6 tracking-tight">Ready to get started?</h3>
            <p className="text-gray-600 mb-8 text-base leading-relaxed">Join thousands of satisfied customers enjoying fresh, delicious meals delivered daily.</p>
            <motion.button 
              onClick={() => {
                const section = document.getElementById('subscription-plans');
                if (section) {
                  window.scrollTo({
                    top: section.offsetTop - 80,
                    behavior: 'smooth'
                  });
                }
              }}
              className="w-full md:w-auto px-12 py-5 bg-gray-900 text-white font-black text-lg border-2 border-gray-900 hover:bg-white hover:text-gray-900 transition-all duration-300 rounded-lg shadow-lg hover:shadow-xl"
              whileHover={{ scale: 1.03, y: -1 }}
              whileTap={{ scale: 0.97 }}
            >
              Choose Your Plan
            </motion.button>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
}
