import React, { useRef, useEffect, useState } from "react";
import { motion, useInView } from "framer-motion";

export default function HowItWorks() {
  const containerRef = useRef(null);
  const isInView = useInView(containerRef, { once: true, amount: 0.2 });

  const handleWhatsAppContact = () => {
    // Replace with your actual WhatsApp number
    const phoneNumber = '+918123456789'; // Update this with your actual WhatsApp number
    const message = encodeURIComponent('Hi! I\'m interested in Myna Kitchen\'s meal plans and would like to discuss my requirements to choose the best plan for me.');
    const whatsappUrl = `https://wa.me/${phoneNumber}?text=${message}`;

    window.open(whatsappUrl, '_blank');
  };

  const steps = [
    {
      number: "01",
      title: "Browse & Choose",
      description: "Explore our delicious meal plans and find the perfect fit for your lifestyle and dietary needs.",
      accent: "#8B4513",
      bgGradient: "bg-gradient-to-br from-orange-50 to-yellow-50",
      illustration: (
        <motion.div 
          className="w-16 h-16 mx-auto flex items-center justify-center"
          whileHover={{ scale: 1.05 }}
          transition={{ duration: 0.3 }}
        >
          <div className="grid grid-cols-2 gap-1 w-14 h-14">
            <div className="bg-gradient-to-br from-orange-200 to-orange-300 border border-orange-400 rounded-lg flex items-center justify-center shadow-sm">
              <span className="text-sm">ü•ó</span>
            </div>
            <div className="bg-gradient-to-br from-green-200 to-green-300 border border-green-400 rounded-lg flex items-center justify-center shadow-sm">
              <span className="text-sm">üçõ</span>
            </div>
            <div className="bg-gradient-to-br from-blue-200 to-blue-300 border border-blue-400 rounded-lg flex items-center justify-center shadow-sm">
              <span className="text-sm">üçú</span>
            </div>
            <div className="bg-gradient-to-br from-yellow-200 to-yellow-300 border border-yellow-400 rounded-lg flex items-center justify-center shadow-sm">
              <span className="text-sm">ü•ò</span>
            </div>
          </div>
        </motion.div>
      )
    },
    {
      number: "02", 
      title: "Connect on WhatsApp",
      description: "Get in touch with our team to discuss your requirements and get personalized meal recommendations.",
      accent: "#059669",
      bgGradient: "bg-gradient-to-br from-green-50 to-emerald-50",
      illustration: (
        <motion.div 
          className="w-16 h-16 mx-auto flex items-center justify-center"
          whileHover={{ scale: 1.05 }}
          transition={{ duration: 0.3 }}
        >
          <div className="w-14 h-14 bg-gradient-to-br from-green-100 to-green-200 border-2 border-green-400 rounded-2xl flex items-center justify-center shadow-lg">
            <div className="relative">
              <motion.div 
                className="w-8 h-8 bg-gradient-to-br from-green-500 to-green-600 rounded-full flex items-center justify-center shadow-md"
                animate={{ scale: [1, 1.05, 1] }}
                transition={{ duration: 2, repeat: Infinity }}
              >
                <span className="text-white text-xs font-bold">üí¨</span>
              </motion.div>
              <motion.div 
                className="absolute -top-1 -right-1 w-3 h-3 bg-green-600 rounded-full"
                animate={{ opacity: [0.7, 1, 0.7] }}
                transition={{ duration: 1.5, repeat: Infinity }}
              />
            </div>
          </div>
        </motion.div>
      ),
      hasButton: true
    },
    {
      number: "03",
      title: "Sign Up & Select",
      description: "Complete your registration and choose your preferred plan with flexible scheduling options.",
      accent: "#7C3AED",
      bgGradient: "bg-gradient-to-br from-purple-50 to-violet-50",
      illustration: (
        <motion.div 
          className="w-16 h-16 mx-auto flex items-center justify-center"
          whileHover={{ scale: 1.05 }}
          transition={{ duration: 0.3 }}
        >
          <div className="bg-gradient-to-br from-purple-100 to-purple-200 border-2 border-purple-400 rounded-xl w-14 h-14 flex flex-col justify-center items-center shadow-lg">
            <motion.div 
              className="w-8 h-1 bg-gradient-to-r from-purple-400 to-purple-500 rounded mb-1"
              initial={{ width: 0 }}
              animate={isInView ? { width: 32 } : { width: 0 }}
              transition={{ duration: 0.8, delay: 0.5 }}
            />
            <motion.div 
              className="w-6 h-1 bg-gradient-to-r from-purple-300 to-purple-400 rounded mb-1"
              initial={{ width: 0 }}
              animate={isInView ? { width: 24 } : { width: 0 }}
              transition={{ duration: 0.8, delay: 0.7 }}
            />
            <motion.div 
              className="w-4 h-4 bg-gradient-to-br from-purple-500 to-purple-600 rounded flex items-center justify-center shadow-sm"
              initial={{ scale: 0 }}
              animate={isInView ? { scale: 1 } : { scale: 0 }}
              transition={{ duration: 0.5, delay: 1 }}
            >
              <span className="text-white text-xs">‚úì</span>
            </motion.div>
          </div>
        </motion.div>
      )
    },
    {
      number: "04",
      title: "Enjoy Daily Deliveries",
      description: "Start your subscription and enjoy fresh, tasty meals delivered to your doorstep every day.",
      accent: "#DC2626",
      bgGradient: "bg-gradient-to-br from-red-50 to-pink-50",
      illustration: (
        <motion.div 
          className="w-16 h-16 mx-auto flex items-center justify-center"
          whileHover={{ scale: 1.05 }}
          transition={{ duration: 0.3 }}
        >
          <div className="relative bg-gradient-to-br from-red-100 to-red-200 border-2 border-red-400 rounded-2xl w-14 h-14 flex items-center justify-center shadow-lg">
            <motion.div 
              className="w-8 h-8 bg-gradient-to-br from-red-500 to-red-600 rounded-full flex items-center justify-center shadow-md"
              animate={{ x: [0, 1, 0] }}
              transition={{ duration: 2, repeat: Infinity }}
            >
              <span className="text-white text-xs">üöö</span>
            </motion.div>
            <motion.div 
              className="absolute top-2 right-2 w-2 h-2 bg-red-600 rounded-full"
              animate={{ scale: [1, 1.1, 1] }}
              transition={{ duration: 1.5, repeat: Infinity }}
            />
            <div className="absolute bottom-2 left-2 w-1 h-1 bg-red-400 rounded-full" />
          </div>
        </motion.div>
      )
    }
  ];

  const StackedCard = ({ step, index, totalCards, isSticky }) => {
    // Special styling for the first card to make it sticky
    const isFirstCard = index === 0;

    return (
      <div 
        className={`w-full max-w-xl mx-auto mb-4 ${isFirstCard ? 'sticky' : 'sticky'}`}
        style={{
          top: isFirstCard ? 'calc(20vh + 0px)' : `${80 + index * 8}px`,
          zIndex: isFirstCard ? 49 : totalCards - index,
        }}
      >
        <motion.div
          className={`${step.bgGradient} border-2 border-gray-900 shadow-2xl overflow-hidden relative w-full flex flex-col rounded-2xl transition-all duration-200 hover:shadow-3xl`}
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true, margin: "-50px" }}
          transition={{ 
            duration: 0.4, 
            delay: index * 0.1,
            ease: "easeOut"
          }}
          whileHover={{ 
            y: -2,
            transition: { duration: 0.15 }
          }}
          style={{ 
            height: "45vh",
            minHeight: "400px",
            transform: 'translateZ(0)',
            backfaceVisibility: 'hidden',
            perspective: '1000px'
          }}
        >
          {/* Accent line */}
          <div 
            className="h-3 w-full flex-shrink-0" 
            style={{backgroundColor: step.accent}}
          />

          {/* Header with sketchy border */}
          <div className="p-4 md:p-6 border-b border-gray-200/50 flex-shrink-0">
            <div 
              className="relative border-2 border-gray-800 rounded-lg p-3 md:p-4 bg-white/40"
              style={{
                borderStyle: 'dashed',
                borderRadius: '12px',
                boxShadow: '2px 2px 0px rgba(0,0,0,0.1)',
                transform: 'rotate(-0.5deg)'
              }}
            >
              <div className="flex items-center gap-3 md:gap-4 justify-center">
                {/* Step number */}
                <motion.div 
                  className="w-10 h-10 md:w-12 md:h-12 bg-gray-900 text-white rounded-full flex items-center justify-center font-black text-sm md:text-lg shadow-lg transition-transform duration-150 flex-shrink-0"
                  whileHover={{ 
                    scale: 1.05,
                    transition: { duration: 0.15 }
                  }}
                >
                  {step.number}
                </motion.div>

                {/* Title */}
                <h3 className="text-lg md:text-xl font-black text-gray-900 tracking-tight leading-tight">
                  {step.title}
                </h3>
              </div>
            </div>
          </div>

          {/* Content area */}
          <div className="p-4 md:p-6 flex flex-col flex-grow">
            {/* Visual illustration */}
            <div className="mb-4 flex-shrink-0 flex justify-center">
              <div className="transform scale-75 md:scale-90">
                {step.illustration}
              </div>
            </div>

            {/* Description and actions */}
            <div className="flex-grow flex flex-col justify-between">
              <p className="text-gray-700 text-sm md:text-base leading-relaxed mb-4 text-center max-w-md mx-auto">
                {step.description}
              </p>

              {/* WhatsApp Button */}
              {step.hasButton && (
                <div className="mb-4 flex-shrink-0">
                  <motion.button
                    onClick={handleWhatsAppContact}
                    className="w-full py-3 md:py-4 bg-gradient-to-r from-green-600 to-green-700 hover:from-green-700 hover:to-green-800 text-white font-bold text-sm md:text-base border-2 border-green-600 hover:border-green-700 transition-all duration-150 flex items-center justify-center gap-2 rounded-lg shadow-lg hover:shadow-xl"
                    whileHover={{ scale: 1.01 }}
                    whileTap={{ scale: 0.99 }}
                  >
                    <motion.span 
                      className="text-base md:text-lg"
                      animate={{ scale: [1, 1.1, 1] }}
                      transition={{ duration: 2, repeat: Infinity }}
                    >
                      üí¨
                    </motion.span>
                    Contact on WhatsApp
                  </motion.button>
                </div>
              )}

              {/* Feature highlight */}
              <div className="border-t border-gray-200/50 pt-4 flex-shrink-0">
                <div className="flex items-center justify-center">
                  <motion.div 
                    className="w-2 h-2 md:w-3 md:h-3 rounded-full mr-2 md:mr-3 flex-shrink-0 shadow-sm" 
                    style={{backgroundColor: step.accent}}
                    animate={{ scale: [1, 1.05, 1] }}
                    transition={{ duration: 2, repeat: Infinity, delay: index * 0.3 }}
                  />
                  <span className="text-xs md:text-sm text-gray-600 uppercase tracking-widest font-bold text-center">
                    {index === 0 && "Multiple Options"}
                    {index === 1 && "Instant Support"}
                    {index === 2 && "Easy Setup"}
                    {index === 3 && "Fresh Daily"}
                  </span>
                </div>
              </div>
            </div>
          </div>
        </motion.div>
      </div>
    );
  };

  return (
    <section id="how-it-works" className="relative" style={{backgroundColor: '#f9f5f0'}}>
      {/* Subtle background decoration */}
      <div className="absolute inset-0 opacity-[0.02]">
        <div className="absolute top-20 left-10 w-32 h-32 bg-gray-900 rounded-full blur-3xl" />
        <div className="absolute bottom-20 right-10 w-40 h-40 bg-gray-900 rounded-full blur-3xl" />
      </div>

      {/* Sticky Header Section */}
      <div className="sticky top-0 z-50 bg-[#f9f5f0] border-b border-gray-200/20">
        <div className="container mx-auto px-4 md:px-6">
          <motion.div 
            className="text-center flex flex-col justify-center py-8 md:py-12"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8 }}
          >
            <motion.h2 
              className="text-3xl md:text-4xl lg:text-5xl font-black text-gray-900 mb-4 tracking-tight"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              How It Works
            </motion.h2>
            <motion.p 
              className="text-base md:text-lg text-gray-600 max-w-2xl mx-auto leading-relaxed"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.8, delay: 0.4 }}
            >
              From browsing to enjoying delicious meals at your doorstep - we've made it incredibly simple in just 4 easy steps.
            </motion.p>
          </motion.div>
        </div>
      </div>

      {/* Sticky First Tile */}
      <div className="sticky top-[120px] z-40 container mx-auto px-4 md:px-6">
        <StackedCard 
          step={steps[0]} 
          index={0} 
          totalCards={steps.length}
          isSticky={true}
        />
      </div>

      {/* Remaining Stacked Cards */}
      <div className="relative container mx-auto px-4 md:px-6" style={{ paddingBottom: "15vh" }}>
        {steps.slice(1).map((step, index) => (
          <StackedCard 
            key={index + 1} 
            step={step} 
            index={index + 1} 
            totalCards={steps.length}
            isSticky={false}
          />
        ))}
      </div>

      {/* Call to Action */}
      <div className="container mx-auto px-4 md:px-6 pb-20 md:pb-28">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 50 }}
          transition={{ duration: 1, delay: 1.2 }}
          className="text-center mt-10"
        >
          <motion.div 
            className="bg-gradient-to-br from-white to-gray-50 border-2 border-gray-900 shadow-2xl p-8 md:p-10 max-w-2xl mx-auto rounded-2xl hover:shadow-3xl transition-all duration-200"
            whileHover={{ y: -2, scale: 1.005 }}
          >
            <h3 className="text-2xl md:text-3xl font-black text-gray-900 mb-4 md:mb-6 tracking-tight">Ready to get started?</h3>
            <p className="text-gray-600 mb-6 md:mb-8 text-sm md:text-base leading-relaxed">Join thousands of satisfied customers enjoying fresh, delicious meals delivered daily.</p>
            <motion.button 
              onClick={() => {
                const section = document.getElementById('subscription-plans');
                if (section) {
                  window.scrollTo({
                    top: section.offsetTop - 80,
                    behavior: 'smooth'
                  });
                }
              }}
              className="w-full md:w-auto px-8 md:px-12 py-4 md:py-5 bg-gray-900 text-white font-black text-base md:text-lg border-2 border-gray-900 hover:bg-white hover:text-gray-900 transition-all duration-200 rounded-lg shadow-lg hover:shadow-xl"
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              Choose Your Plan
            </motion.button>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
}
