import React, { useRef, useEffect, useState } from "react";
import { motion, useInView, useScroll, useTransform, useMotionValue } from "framer-motion";

export default function HowItWorks() {
  const containerRef = useRef(null);
  const stackedCardsRef = useRef(null);
  const isInView = useInView(containerRef, { once: true, amount: 0.2 });

  // Scroll progress for the entire section
  const { scrollYProgress } = useScroll({
    target: stackedCardsRef,
    offset: ["start center", "end center"]
  });

  const handleWhatsAppContact = () => {
    // Replace with your actual WhatsApp number
    const phoneNumber = '+918123456789'; // Update this with your actual WhatsApp number
    const message = encodeURIComponent('Hi! I\'m interested in Myna Kitchen\'s meal plans and would like to discuss my requirements to choose the best plan for me.');
    const whatsappUrl = `https://wa.me/${phoneNumber}?text=${message}`;

    window.open(whatsappUrl, '_blank');
  };

  const steps = [
    {
      number: "01",
      title: "Browse Plans",
      description: "Explore our delicious meal plans and find the perfect fit for your lifestyle and dietary needs.",
      accent: "#49111C",
      bgGradient: "bg-gradient-to-br from-stone-50 to-stone-100",
      bgColor: "#F2F4F3",
      image: "/images/how-it-works/1.png",
      imageAlt: "Browse meal plans interface showing various food options"
    },
    {
      number: "02", 
      title: "Connect on WhatsApp",
      description: "Get in touch with our team to discuss your requirements and get personalized meal recommendations.",
      accent: "#5E503F",
      bgGradient: "bg-gradient-to-br from-stone-50 to-stone-100", 
      bgColor: "#A9927D",
      image: "/images/how-it-works/2.png",
      imageAlt: "WhatsApp chat interface for customer support",
      hasButton: true
    },
    {
      number: "03",
      title: "Easy Registration",
      description: "Complete your registration using our simple form and receive your personalized user dashboard via email. Sign up takes just a few minutes and you'll get instant access to your account.",
      accent: "#49111C",
      bgGradient: "bg-gradient-to-br from-stone-50 to-stone-100",
      bgColor: "#F2F4F3",
      image: "/images/how-it-works/3.png",
      imageAlt: "User registration form and dashboard preview"
    },
    {
      number: "04",
      title: "Manage Everything",
      description: "Use your personalized dashboard to manage everything - add or cancel subscriptions, change delivery addresses, order delicious add-ons, and customize your meal preferences all in one place.",
      accent: "#5E503F",
      bgGradient: "bg-gradient-to-br from-stone-50 to-stone-100",
      bgColor: "#A9927D",
      image: "/images/how-it-works/4.png",
      imageAlt: "User dashboard showing subscription management features"
    }
  ];

  const StackedCard = ({ step, index, totalCards }) => {
    const cardRef = useRef(null);

    // Card-specific scroll tracking
    const { scrollYProgress: cardScrollProgress } = useScroll({
      target: cardRef,
      offset: ["start center", "end center"]
    });

    // Calculate progress through the entire stacked section
    const cardProgress = useTransform(
      scrollYProgress,
      [index / totalCards, (index + 1) / totalCards],
      [0, 1]
    );

    // Stacking rotation and positioning
    const stackRotate = useTransform(cardProgress, [0, 0.5, 1], [
      0, // No initial rotation - keep cards straight
      0, // Maintain straight position
      0  // No rotation on exit - keep straight
    ]);

    const stackY = useTransform(cardProgress, [0, 0.5, 1], [
      0,           // Initial position
      0,           // Hold position
      -window.innerHeight * 1.2 // Slide up and out
    ]);

    const stackOpacity = useTransform(cardProgress, [0, 0.8, 1], [1, 1, 0]);

    const stackScale = useTransform(cardProgress, [0, 0.7, 1], [
      1 - (index * 0.02), // Slightly smaller for stacked effect
      1 - (index * 0.02), // Maintain scale
      0.8                  // Shrink on exit
    ]);

    return (
      <motion.div 
        ref={cardRef}
        className="sticky w-full max-w-4xl mx-auto"
        style={{
          top: `${80 + index * 4}px`, // Slightly offset each card
          zIndex: totalCards - index + 10,
          rotateZ: stackRotate,
          y: stackY,
          opacity: stackOpacity,
          scale: stackScale
        }}
      >
        <motion.div 
          className="border-2 shadow-2xl rounded-3xl overflow-hidden hover:shadow-3xl transition-all duration-300"
          style={{ 
            backgroundColor: step.bgColor,
            borderColor: "#0A0908"
          }}
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6, delay: index * 0.1 }}
          whileHover={{ y: -4, scale: 1.002 }}
        >
          {/* Card Header with Step Number */}
          <div className="px-6 md:px-8 pt-6 md:pt-8 pb-4">
            <div className="flex items-center justify-between mb-4">
              <motion.div 
                className="flex items-center justify-center w-12 h-12 md:w-16 md:h-16 rounded-full border-3 shadow-lg"
                style={{ 
                  backgroundColor: step.accent,
                  borderColor: "#0A0908"
                }}
                whileHover={{ scale: 1.05 }}
                transition={{ duration: 0.3 }}
              >
                <span className="font-black text-xl md:text-2xl" style={{ color: "#F2F4F3" }}>{step.number}</span>
              </motion.div>
              <div className="flex-1 ml-4 md:ml-6">
                <h3 className="text-2xl md:text-3xl lg:text-4xl font-black mb-2 tracking-tight" style={{ color: "#0A0908" }}>
                  {step.title}
                </h3>
              </div>
            </div>
          </div>

          {/* Main Content Area */}
          <div className="grid lg:grid-cols-2 gap-6 md:gap-8 px-6 md:px-8 pb-6 md:pb-8">
            {/* Image Section */}
            <div className="order-2 lg:order-1 flex items-center justify-center">
              <motion.div
                className="relative w-full max-w-md aspect-square"
                initial={{ opacity: 0, scale: 0.8 }}
                whileInView={{ opacity: 1, scale: 1 }}
                viewport={{ once: true }}
                transition={{ duration: 0.6, delay: 0.2 }}
                whileHover={{ scale: 1.02 }}
              >
                <div className="absolute inset-0 rounded-2xl" style={{ backgroundColor: "rgba(94, 80, 63, 0.1)" }} />
                <img
                  src={step.image}
                  alt={step.imageAlt}
                  className="w-full h-full object-cover rounded-2xl shadow-xl border-2"
                  style={{ borderColor: "#5E503F" }}
                  loading="lazy"
                />
                {/* Decorative overlay */}
                <div className="absolute inset-0 rounded-2xl" style={{ background: "linear-gradient(to top, rgba(10, 9, 8, 0.1), transparent)" }} />
              </motion.div>
            </div>

            {/* Text Content Section */}
            <div className="order-1 lg:order-2 flex flex-col justify-center space-y-6">
              <motion.p 
                className="text-base md:text-lg lg:text-xl leading-relaxed"
                style={{ color: "#5E503F" }}
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.6, delay: 0.3 }}
              >
                {step.description}
              </motion.p>

              {/* WhatsApp Button */}
              {step.hasButton && (
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.6, delay: 0.4 }}
                >
                  <motion.button
                    onClick={handleWhatsAppContact}
                    className="w-full lg:w-auto px-6 md:px-8 py-3 md:py-4 font-bold text-sm md:text-base border-2 transition-all duration-150 flex items-center justify-center gap-3 rounded-xl shadow-lg hover:shadow-xl"
                    style={{ 
                      backgroundColor: "#49111C",
                      color: "#F2F4F3",
                      borderColor: "#0A0908"
                    }}
                    whileHover={{ 
                      scale: 1.02,
                      backgroundColor: "#5E503F"
                    }}
                    whileTap={{ scale: 0.98 }}
                  >
                    <motion.span 
                      className="text-base md:text-lg"
                      animate={{ scale: [1, 1.1, 1] }}
                      transition={{ duration: 2, repeat: Infinity }}
                    >
                      ðŸ’¬
                    </motion.span>
                    <span>Contact on WhatsApp</span>
                  </motion.button>
                </motion.div>
              )}

              {/* Feature highlight */}
              <motion.div 
                className="border-t pt-4"
                style={{ borderColor: "rgba(94, 80, 63, 0.3)" }}
                initial={{ opacity: 0 }}
                whileInView={{ opacity: 1 }}
                viewport={{ once: true }}
                transition={{ duration: 0.6, delay: 0.5 }}
              >
                <div className="flex items-center">
                  <motion.div 
                    className="w-3 h-3 rounded-full mr-3 shadow-sm" 
                    style={{backgroundColor: step.accent}}
                    animate={{ scale: [1, 1.05, 1] }}
                    transition={{ duration: 2, repeat: Infinity, delay: index * 0.3 }}
                  />
                  <span className="text-sm md:text-base uppercase tracking-wider font-bold" style={{ color: "#5E503F" }}>
                    {index === 0 && "Multiple Plans Available"}
                    {index === 1 && "Instant Support Available"}
                    {index === 2 && "Quick & Easy Process"}
                    {index === 3 && "Complete Control & Flexibility"}
                  </span>
                </div>
              </motion.div>
            </div>
          </div>
        </motion.div>
      </motion.div>
    );
  };

  return (
    <section id="how-it-works" className="relative" style={{backgroundColor: '#A9927D'}}>
      {/* Subtle background decoration */}
      <div className="absolute inset-0 opacity-[0.02]">
        <div className="absolute top-20 left-10 w-32 h-32 rounded-full blur-3xl" style={{ backgroundColor: "#0A0908" }} />
        <div className="absolute bottom-20 right-10 w-40 h-40 rounded-full blur-3xl" style={{ backgroundColor: "#0A0908" }} />
      </div>

      {/* Header Section */}
      <div className="container mx-auto px-4 md:px-6">
        <motion.div 
          className="text-center flex flex-col justify-center py-12 md:py-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          <motion.h2 
            className="text-3xl md:text-4xl lg:text-5xl font-black mb-4 tracking-tight"
            style={{ color: "#0A0908" }}
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            How It Works
          </motion.h2>
          <motion.p 
            className="text-base md:text-lg max-w-2xl mx-auto leading-relaxed"
            style={{ color: "#5E503F" }}
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            From browsing to enjoying delicious meals at your doorstep - we've made it incredibly simple in just 4 easy steps.
          </motion.p>
        </motion.div>
      </div>

      {/* Stacked Cards Container */}
      <div 
        ref={stackedCardsRef} 
        className="relative px-4 md:px-6 pb-20"
        style={{ height: `${steps.length * 150}vh` }}
      >
        <div ref={containerRef} className="relative h-full">
          {steps.map((step, index) => (
            <StackedCard 
              key={index}
              step={step} 
              index={index} 
              totalCards={steps.length}
            />
          ))}
        </div>
      </div>

      {/* Call to Action */}
      <div className="container mx-auto px-4 md:px-6 pb-16 md:pb-24">
        <motion.div 
          className="text-center"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          <motion.div 
            className="border-2 shadow-2xl p-8 md:p-10 max-w-2xl mx-auto rounded-2xl hover:shadow-3xl transition-all duration-200"
            style={{ 
              backgroundColor: "#F2F4F3",
              borderColor: "#0A0908"
            }}
            whileHover={{ y: -2, scale: 1.005 }}
          >
            <h3 className="text-2xl md:text-3xl font-black mb-4 md:mb-6 tracking-tight" style={{ color: "#0A0908" }}>Ready to get started?</h3>
            <p className="mb-6 md:mb-8 text-sm md:text-base leading-relaxed" style={{ color: "#5E503F" }}>Join hundreds of satisfied customers enjoying fresh, delicious meals delivered daily.</p>
            <motion.button 
              onClick={() => {
                const section = document.getElementById('subscription-plans');
                if (section) {
                  window.scrollTo({
                    top: section.offsetTop - 80,
                    behavior: 'smooth'
                  });
                }
              }}
              className="w-full md:w-auto px-8 md:px-12 py-4 md:py-5 font-black text-base md:text-lg border-2 transition-all duration-200 rounded-lg shadow-lg hover:shadow-xl"
              style={{ 
                backgroundColor: "#0A0908",
                color: "#F2F4F3",
                borderColor: "#0A0908"
              }}
              whileHover={{ 
                scale: 1.02,
                backgroundColor: "#F2F4F3",
                color: "#0A0908"
              }}
              whileTap={{ scale: 0.98 }}
            >
              Sign up now
            </motion.button>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
