import React, { useEffect, useRef, useState } from 'react';
import { motion, useScroll, useTransform, useSpring, useInView } from 'framer-motion';
import './ProblemStatement.css';

// Typewriter effect component
const TypewriterText = ({ text, delay = 0 }) => {
  const [displayedText, setDisplayedText] = useState('');
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const timer = setTimeout(() => {
      if (currentIndex < text.length) {
        setDisplayedText(prev => prev + text[currentIndex]);
        setCurrentIndex(prev => prev + 1);
      }
    }, delay + currentIndex * 100);

    return () => clearTimeout(timer);
  }, [currentIndex, text, delay]);

  return (
    <span>
      {displayedText}
      <motion.span
        className="typewriter-cursor"
        animate={{ opacity: [1, 0] }}
        transition={{ duration: 0.8, repeat: Infinity }}
      >
        |
      </motion.span>
    </span>
  );
};

const ProblemStatement = () => {
  const ref = useRef(null);
  const { scrollYProgress } = useScroll({
    target: ref,
    offset: ["start end", "end start"]
  });

  // Enhanced parallax effects
  const y = useTransform(scrollYProgress, [0, 1], [-50, 50]);
  const opacity = useTransform(scrollYProgress, [0, 0.2, 0.8, 1], [0, 1, 1, 0]);

  // Floating animation for decorative elements
  const floatingY = useSpring(useTransform(scrollYProgress, [0, 1], [0, -20]), {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });

  const problems = [
    {
      id: 'busy',
      title: 'Too Busy to Cook?',
      description: 'Your schedule is packed. Between work, commute, and life, who has time to plan, shop, and cook?',
      icon: 'â°',
      fact: 'Average person works 8+ hours daily'
    },
    {
      id: 'junk',
      title: 'Stuck with Junk Food?',
      description: 'Quick fixes lead to unhealthy choices. Fast food becomes the norm when convenience matters most.',
      icon: 'ðŸ”',
      fact: '70% of daily intake is processed food'
    },
    {
      id: 'expensive',
      title: 'Tired of Overspending on Food?',
      description: 'Ordering out daily burns through your budget. Groceries expire unused. Money down the drain.',
      icon: 'ðŸ’¸',
      fact: 'Spend â‚¹500+ daily on food delivery'
    },
    {
      id: 'away',
      title: 'Away from Home?',
      description: 'Living away from family means no home-cooked meals. Missing that comfort and nutrition.',
      icon: 'ðŸ ',
      fact: 'Missing the comfort of home-cooked meals'
    }
  ];

  return (
    <div ref={ref}>
      {/* Enhanced Hero Section with Futuristic Design */}
      <section className="futuristic-hero-section">
        <div className="hero-background-container">
          {/* Animated Background Elements */}
          <motion.div 
            className="floating-orb orb-1"
            style={{ y: floatingY }}
            animate={{ 
              rotate: 360,
              scale: [1, 1.1, 1],
            }}
            transition={{ 
              rotate: { duration: 20, repeat: Infinity, ease: "linear" },
              scale: { duration: 4, repeat: Infinity, ease: "easeInOut" }
            }}
          />
          <motion.div 
            className="floating-orb orb-2"
            style={{ y: useTransform(floatingY, [0, -20], [0, 15]) }}
            animate={{ 
              rotate: -360,
              scale: [1.1, 1, 1.1],
            }}
            transition={{ 
              rotate: { duration: 25, repeat: Infinity, ease: "linear" },
              scale: { duration: 6, repeat: Infinity, ease: "easeInOut" }
            }}
          />
          <motion.div 
            className="floating-orb orb-3"
            style={{ y: useTransform(floatingY, [0, -20], [0, -10]) }}
            animate={{ 
              rotate: 360,
              scale: [1, 1.2, 1],
            }}
            transition={{ 
              rotate: { duration: 30, repeat: Infinity, ease: "linear" },
              scale: { duration: 8, repeat: Infinity, ease: "easeInOut" }
            }}
          />

          {/* Particle Effects */}
          <div className="particles-container">
            {[...Array(12)].map((_, i) => (
              <motion.div
                key={i}
                className="particle"
                style={{
                  position: 'absolute',
                  width: Math.random() * 4 + 2 + 'px',
                  height: Math.random() * 4 + 2 + 'px',
                  left: Math.random() * 100 + '%',
                  top: Math.random() * 100 + '%',
                }}
                animate={{
                  y: [0, -30, 0],
                  opacity: [0, 1, 0],
                  scale: [0, 1, 0],
                }}
                transition={{
                  duration: 3 + Math.random() * 2,
                  repeat: Infinity,
                  delay: Math.random() * 2,
                  ease: "easeInOut"
                }}
              />
            ))}
          </div>
        </div>

        <div className="container mx-auto px-4 md:px-6 relative z-10">
          <motion.div 
            className="futuristic-hero-content"
            style={{ y, opacity }}
          >
            <div className="text-center mb-8">
              {/* Glowing accent line */}
              <motion.div 
                className="hero-accent-line"
                initial={{ width: 0, opacity: 0 }}
                whileInView={{ width: "100px", opacity: 1 }}
                viewport={{ once: true }}
                transition={{ duration: 1, delay: 0.2 }}
              />

              {/* Enhanced main title with gradient and glow effects */}
              <motion.h2 
                className="futuristic-hero-title"
                initial={{ opacity: 0, y: 30, scale: 0.9 }}
                whileInView={{ opacity: 1, y: 0, scale: 1 }}
                viewport={{ once: true }}
                transition={{ 
                  duration: 0.8, 
                  delay: 0.1, 
                  ease: "easeOut",
                  type: "spring",
                  stiffness: 100 
                }}
              >
                <span className="hero-text-myna">Myna</span>
                <motion.span 
                  className="hero-text-kitchen"
                  initial={{ opacity: 0, x: 20 }}
                  whileInView={{ opacity: 1, x: 0 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.6, delay: 0.4 }}
                >
                  Kitchen
                </motion.span>
              </motion.h2>

              {/* Animated subtitle with typewriter effect */}
              <motion.h3 
                className="futuristic-hero-subtitle"
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.6, delay: 0.3 }}
              >
                <TypewriterText text="Daily Meals Delivered" />
              </motion.h3>

              {/* Enhanced description with stagger animation */}
              <motion.div
                className="futuristic-description-container"
                initial={{ opacity: 0 }}
                whileInView={{ opacity: 1 }}
                viewport={{ once: true }}
                transition={{ duration: 0.8, delay: 0.5 }}
              >
                <motion.p 
                  className="futuristic-hero-description"
                  variants={{
                    hidden: { opacity: 0, y: 20 },
                    visible: {
                      opacity: 1,
                      y: 0,
                      transition: {
                        staggerChildren: 0.05,
                        delayChildren: 0.6
                      }
                    }
                  }}
                  initial="hidden"
                  whileInView="visible"
                  viewport={{ once: true }}
                >
                  {"With us you no longer have to run a kitchen, manage groceries or worry about your daily food.".split(" ").map((word, index) => (
                    <motion.span
                      key={index}
                      variants={{
                        hidden: { opacity: 0, y: 10 },
                        visible: { opacity: 1, y: 0 }
                      }}
                      className="inline-block mr-2"
                    >
                      {word}
                    </motion.span>
                  ))}
                </motion.p>

                {/* Enhanced sub-description */}
                <motion.p 
                  className="futuristic-hero-subdescription"
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.6, delay: 0.8 }}
                >
                  Fresh homely meals delivered to you day by day, meal by meal
                </motion.p>
              </motion.div>

              {/* Enhanced pricing card with futuristic design */}
              <motion.div
                className="futuristic-pricing-hero-card"
                initial={{ opacity: 0, y: 50, scale: 0.9 }}
                whileInView={{ opacity: 1, y: 0, scale: 1 }}
                viewport={{ once: true }}
                transition={{ 
                  duration: 0.8, 
                  delay: 0.9,
                  type: "spring",
                  stiffness: 100
                }}
                whileHover={{ 
                  y: -8,
                  boxShadow: "0 25px 50px rgba(0, 0, 0, 0.15)",
                  transition: { duration: 0.3 }
                }}
              >
                <div className="pricing-glow-border" />
                <div className="pricing-content relative z-10">
                  <motion.div 
                    className="pricing-badge-futuristic"
                    initial={{ opacity: 0, scale: 0 }}
                    whileInView={{ opacity: 1, scale: 1 }}
                    viewport={{ once: true }}
                    transition={{ duration: 0.5, delay: 1.1 }}
                  >
                    STARTING FROM
                  </motion.div>

                  <div className="pricing-main-futuristic">
                    <motion.span 
                      className="price-amount-futuristic"
                      initial={{ opacity: 0, scale: 0.5 }}
                      whileInView={{ opacity: 1, scale: 1 }}
                      viewport={{ once: true }}
                      transition={{ 
                        duration: 0.6, 
                        delay: 1.2,
                        type: "spring",
                        stiffness: 150
                      }}
                    >
                      â‚¹80
                    </motion.span>
                    <motion.span 
                      className="price-period-futuristic"
                      initial={{ opacity: 0, x: -20 }}
                      whileInView={{ opacity: 1, x: 0 }}
                      viewport={{ once: true }}
                      transition={{ duration: 0.5, delay: 1.4 }}
                    >
                      /day
                    </motion.span>
                  </div>

                  <motion.button 
                    className="futuristic-cta-button"
                    initial={{ opacity: 0, y: 20 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    transition={{ duration: 0.5, delay: 1.5 }}
                    whileHover={{ 
                      scale: 1.05,
                      boxShadow: "0 10px 30px rgba(188, 47, 43, 0.3)",
                      transition: { duration: 0.2 }
                    }}
