import React, { useEffect, useState, useRef } from 'react';
import { motion } from 'framer-motion';
import './DailyMealsDelivered.css';

const DailyMealsDelivered = () => {
  const [scrollY, setScrollY] = useState(0);
  const sectionRef = useRef(null);

  const cards = [
    {
      id: 1,
      title: 'Daily Meals Delivered',
      subtitle: 'Fresh, home-style meals prepared daily',
      description: 'Fresh, home-style meals prepared daily and delivered right to your doorstep. No more planning, shopping, or cooking required.',
      icon: 'ðŸ½ï¸',
      color: '#FF6B6B'
    },
    {
      id: 2,
      title: 'Save Your Time',
      subtitle: 'Focus on what matters most',
      description: 'No more planning, shopping, or cooking. We handle everything while you focus on what matters most to you.',
      icon: 'â°',
      color: '#4ECDC4'
    },
    {
      id: 3,
      title: 'Budget-Friendly',
      subtitle: 'Starting at â‚¹120/day',
      description: 'More affordable than ordering out, with zero waste. Get premium quality meals at unbeatable prices.',
      icon: 'ðŸ’°',
      color: '#45B7D1'
    }
  ];

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll, { passive: true });

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const getStickyLinePosition = () => {
    const subscriptionSection = document.getElementById('subscription-plans');
    if (subscriptionSection) {
      return subscriptionSection.offsetTop + subscriptionSection.offsetHeight + 50;
    }
    return 800;
  };

  const getCardPosition = (index) => {
    const stickyLineTop = getStickyLinePosition();
    const headerHeight = 80;
    const stackOffset = 60; // How much each card is offset from the previous one

    // Calculate when this card should start sticking
    const cardTriggerStart = stickyLineTop + (index * 200); // Reduced spacing for quicker triggers

    // Calculate the sticky position for this card
    const stickyTop = headerHeight + (index * stackOffset);

    // Calculate when sticky behavior should end
    const sectionElement = sectionRef.current;
    if (!sectionElement) {
      return {
        position: 'absolute',
        top: `${index * 450}px`,
        left: '50%',
        transform: 'translateX(-50%)',
        zIndex: cards.length - index
      };
    }

    const sectionBottom = sectionElement.offsetTop + sectionElement.offsetHeight;
    const endTrigger = sectionBottom - window.innerHeight - 200;

    // Debug logging
    if (index === 0) {
      console.log('Card 0 - ScrollY:', scrollY, 'TriggerStart:', cardTriggerStart, 'StickyLineTop:', stickyLineTop);
    }

    if (scrollY < cardTriggerStart) {
      // Card is in normal document flow
      return {
        position: 'absolute',
        top: `${index * 450}px`,
        left: '50%',
        transform: 'translateX(-50%)',
        zIndex: cards.length - index
      };
    } else if (scrollY >= cardTriggerStart && scrollY < endTrigger) {
      // Card is sticky
      return {
        position: 'fixed',
        top: `${stickyTop}px`,
        left: '50%',
        transform: 'translateX(-50%)',
        zIndex: cards.length - index
      };
    } else {
      // End of sticky behavior
      const offset = scrollY - endTrigger;
      return {
        position: 'absolute',
        top: `${endTrigger - sectionElement.offsetTop + stickyTop + offset}px`,
        left: '50%',
        transform: 'translateX(-50%)',
        zIndex: cards.length - index
      };
    }
  };

  const isCardSticky = (index) => {
    const stickyLineTop = getStickyLinePosition();
    const cardTriggerStart = stickyLineTop + (index * 200);
    const sectionElement = sectionRef.current;

    if (!sectionElement) return false;

    const sectionBottom = sectionElement.offsetTop + sectionElement.offsetHeight;
    const endTrigger = sectionBottom - window.innerHeight - 200;

    return scrollY >= cardTriggerStart && scrollY < endTrigger;
  };

  return (
    <section ref={sectionRef} id="daily-meals-delivered" className="daily-meals-delivered-section">
      {/* Sticky line indicator */}
      <div 
        className="sticky-line" 
        style={{ 
          top: `${getStickyLinePosition()}px`,
          opacity: scrollY > getStickyLinePosition() - 100 ? 1 : 0
        }}
      ></div>

      <div className="container mx-auto px-4 md:px-6">
        {/* Section Header */}
        <motion.div 
          className="text-center mb-20 pt-20"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          <motion.h2 
            className="text-4xl md:text-5xl font-bold mb-6 text-gray-900 tracking-tight"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            Why Choose <span className="text-primary">Myna Kitchen</span>
          </motion.h2>
          <motion.p 
            className="text-xl text-gray-600 max-w-3xl mx-auto"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            Experience the convenience of fresh, home-style meals delivered daily to your doorstep.
          </motion.p>
        </motion.div>

        {/* Stacking Cards Container */}
        <div className="stacking-cards-wrapper">
          {cards.map((card, index) => {
            const cardPosition = getCardPosition(index);
            const isSticky = isCardSticky(index);

            return (
              <motion.div
                key={card.id}
                className={`stacking-card ${isSticky ? 'is-sticky' : ''}`}
                style={{
                  ...cardPosition,
                  backgroundColor: card.color,
                  width: '90%',
                  maxWidth: '600px'
                }}
                initial={{ opacity: 0, y: 50 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.6, delay: index * 0.2 }}
              >
                <div className="card-content">
                  <div className="card-icon">{card.icon}</div>
                  <h3 className="card-title">{card.title}</h3>
                  <p className="card-subtitle">{card.subtitle}</p>
                  <p className="card-description">{card.description}</p>
                </div>

                {/* Card header that shows when stacked */}
                {index > 0 && (
                  <div className="card-header-overlay">
                    <div className="card-icon-small">{card.icon}</div>
                    <h4 className="card-title-small">{card.title}</h4>
                  </div>
                )}
              </motion.div>
            );
          })}
        </div>

        {/* Spacer to ensure proper scrolling */}
        <div style={{ height: `${cards.length * 600}px` }}></div>
      </div>
    </section>
  );
};

export default DailyMealsDelivered;
