import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import './ProblemStatement.css';

const ProblemStatement = () => {
  const [stickyState, setStickyState] = useState({
    headerSticky: false,
    cellsSticky: false,
    activeCell: 0
  });

  const problems = [
    {
      id: 'busy',
      title: 'Too Busy to Cook',
      description: 'Your schedule is packed. Between work, commute, and life, who has time to plan, shop, and cook?',
      icon: 'â°',
      fact: 'Average person works 8+ hours daily'
    },
    {
      id: 'junk',
      title: 'Stuck with Junk Food',
      description: 'Quick fixes lead to unhealthy choices. Fast food becomes the norm when convenience matters most.',
      icon: 'ðŸ”',
      fact: '70% of daily intake is processed food'
    },
    {
      id: 'expensive',
      title: 'Expensive & Wasteful',
      description: 'Ordering out daily burns through your budget. Groceries expire unused. Money down the drain.',
      icon: 'ðŸ’¸',
      fact: 'Spend â‚¹500+ daily on food delivery'
    },
    {
      id: 'away',
      title: 'Away from Home',
      description: 'Living away from family means no home-cooked meals. Missing that comfort and nutrition.',
      icon: 'ðŸ ',
      fact: 'Missing the comfort of home-cooked meals'
    }
  ];

  const solutionFeatures = [
    {
      icon: 'ðŸ½ï¸',
      title: 'Daily Meals Delivered',
      description: 'Fresh, home-style meals prepared daily and delivered right to your doorstep',
      gradient: 'from-blue-500 to-purple-600',
      bgColor: 'bg-blue-50',
      iconBg: 'bg-blue-100'
    },
    {
      icon: 'â°',
      title: 'Save Your Time',
      description: 'No more planning, shopping, or cooking. We handle everything while you focus on what matters',
      gradient: 'from-green-500 to-teal-600',
      bgColor: 'bg-green-50',
      iconBg: 'bg-green-100'
    },
    {
      icon: 'ðŸ’°',
      title: 'Budget-Friendly',
      description: 'Starting at â‚¹120/day - more affordable than ordering out, with zero waste',
      gradient: 'from-yellow-500 to-orange-600',
      bgColor: 'bg-yellow-50',
      iconBg: 'bg-yellow-100'
    },
    {
      icon: 'ðŸ ',
      title: 'Home-Style Comfort',
      description: 'Taste the comfort of home-cooked meals, even when you\'re away from family',
      gradient: 'from-pink-500 to-red-600',
      bgColor: 'bg-pink-50',
      iconBg: 'bg-pink-100'
    }
  ];

  useEffect(() => {
    let ticking = false;
    let lastScrollY = 0;
    let elements = {};

    // Cache DOM elements to avoid repeated queries
    const cacheElements = () => {
      elements = {
        featuresSection: document.getElementById('features'),
        mainHeaderSection: document.querySelector('.main-header-section'),
        lastCell: document.querySelector('.cell-4'),
        problemSection: document.getElementById('problem-statement'),
        dailyMenuSection: document.getElementById('daily-menu')
      };
    };

    const handleScroll = () => {
      if (!ticking) {
        requestAnimationFrame(() => {
          const scrollY = window.scrollY;
          const windowHeight = window.innerHeight;
          const scrollDirection = scrollY > lastScrollY ? 'down' : 'up';
          lastScrollY = scrollY;

          // Re-cache elements if any are missing
          if (!elements.featuresSection || !elements.mainHeaderSection || !elements.lastCell || !elements.problemSection) {
            cacheElements();
            if (!elements.featuresSection || !elements.mainHeaderSection || !elements.lastCell || !elements.problemSection) {
              ticking = false;
              return;
            }
          }

          // Calculate boundaries with buffer zones to prevent flickering
          const bufferZone = 20; // Reduced buffer for more precise control
          const featuresSectionTop = elements.featuresSection.offsetTop;
          const featuresSectionBottom = elements.featuresSection.offsetTop + elements.featuresSection.offsetHeight;
          const mainHeaderTop = elements.mainHeaderSection.offsetTop;
          const mainHeaderBottom = elements.mainHeaderSection.offsetTop + elements.mainHeaderSection.offsetHeight;
          const lastCellBottom = elements.lastCell.offsetTop + elements.lastCell.offsetHeight;
          const problemSectionBottom = elements.problemSection.offsetTop + elements.problemSection.offsetHeight;

          // Calculate the exact point where sticky should end
          // This should be when the bottom of the last tile crosses the bottom of the fixed section
          const stickyHeaderHeight = elements.mainHeaderSection.offsetHeight;
          const stickyLineHeight = 80;
          const totalFixedHeight = stickyHeaderHeight + stickyLineHeight;

          // The sticky section should end when the last cell bottom reaches the bottom of the viewport
          // minus the height of the fixed elements
          const stickyEndPoint = lastCellBottom - windowHeight + totalFixedHeight;

          // Add buffer to daily menu section if it exists, but make it tighter
          let dailyMenuTop = featuresSectionBottom;
          if (elements.dailyMenuSection) {
            dailyMenuTop = elements.dailyMenuSection.offsetTop;
          }

          // Header sticky logic with precise end point
          const headerStickyStart = problemSectionBottom - bufferZone;
          const headerStickyEnd = Math.min(stickyEndPoint, dailyMenuTop - bufferZone);

          let headerSticky = false;
          if (scrollDirection === 'down') {
            headerSticky = scrollY >= headerStickyStart && scrollY < headerStickyEnd;
          } else {
            headerSticky = scrollY >= (headerStickyStart + bufferZone) && scrollY < headerStickyEnd;
          }

          // Cells sticky logic with precise end point
          const cellsStickyStart = mainHeaderBottom;
          const cellsStickyEnd = Math.min(stickyEndPoint, dailyMenuTop - bufferZone);

          let cellsSticky = false;
          if (scrollDirection === 'down') {
            cellsSticky = scrollY >= cellsStickyStart && 
                         scrollY < cellsStickyEnd &&
                         lastCellBottom > scrollY + windowHeight;
          } else {
            cellsSticky = scrollY >= (cellsStickyStart + bufferZone) && 
                         scrollY < cellsStickyEnd &&
                         lastCellBottom > scrollY + windowHeight;
          }

          // Update CSS custom properties for dynamic positioning
          if (headerSticky) {
            const stickyHeaderHeight = elements.mainHeaderSection.offsetHeight;
            const stickyLineTop = stickyHeaderHeight + 20;
            const stickyLineBottom = stickyLineTop + 80;

            document.documentElement.style.setProperty('--sticky-header-height', `${stickyLineTop}px`);
            document.documentElement.style.setProperty('--sticky-line-bottom', `${stickyLineBottom}px`);

            // Add padding smoothly to prevent content jump
            if (!document.body.style.paddingTop) {
              document.body.style.transition = 'padding-top 0.3s ease';
              document.body.style.paddingTop = `${stickyHeaderHeight}px`;
            }
          } else {
            // Remove padding smoothly when not sticky
            if (document.body.style.paddingTop) {
              document.body.style.transition = 'padding-top 0.3s ease';
              document.body.style.paddingTop = '';
              // Clean up transition after animation
              setTimeout(() => {
                if (!document.body.style.paddingTop) {
                  document.body.style.transition = '';
                }
              }, 300);
            }
          }

          // Determine active cell based on scroll position with smoother transitions
          let activeCell = 0;
          if (cellsSticky) {
            const totalScrollDistance = lastCellBottom - mainHeaderBottom - windowHeight;
            const currentScrollDistance = scrollY - mainHeaderBottom;
            const scrollProgress = Math.max(0, Math.min(1, currentScrollDistance / totalScrollDistance));

            // Use smoother thresholds for cell transitions
            if (scrollProgress > 0.2) activeCell = 1;
            if (scrollProgress > 0.45) activeCell = 2;
            if (scrollProgress > 0.7) activeCell = 3;
          }

          // Only update state if values have actually changed
          setStickyState(prevState => {
            if (prevState.headerSticky !== headerSticky || 
                prevState.cellsSticky !== cellsSticky || 
                prevState.activeCell !== activeCell) {
              return {
                headerSticky,
                cellsSticky,
                activeCell
              };
            }
            return prevState;
          });

          ticking = false;
        });
        ticking = true;
      }
    };

    // Initial setup
    cacheElements();
    handleScroll();

    // Add scroll listener with passive option for better performance
    window.addEventListener('scroll', handleScroll, { passive: true });

    // Re-cache elements on resize to handle dynamic content
    const handleResize = () => {
      cacheElements();
      handleScroll();
    };
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('scroll', handleScroll);
      window.removeEventListener('resize', handleResize);
      // Clean up padding and transitions on unmount
      document.body.style.paddingTop = '';
      document.body.style.transition = '';
    };
  }, []);

  return (
    <>
      {/* Problem Statement Section */}
      <section id="problem-statement" className="problem-section">
        <div className="container mx-auto px-4 md:px-6">
          {/* Problem Section Header */}
          <motion.div 
            className="text-center mb-16"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8 }}
          >
            <motion.h2 
              className="problem-title"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              We Know Your <span className="text-red-600">Daily Struggle</span>
            </motion.h2>
            <motion.p 
              className="problem-subtitle"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.8, delay: 0.4 }}
            >
              Modern life shouldn't mean compromising on nutrition and taste
            </motion.p>
          </motion.div>

          {/* Problems List */}
          <div className="problems-list">
            {problems.map((problem, index) => (
              <motion.div
                key={problem.id}
                className="problem-item"
                initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
                whileInView={{ opacity: 1, x: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.8, delay: index * 0.2 }}
              >
                <motion.div 
                  className="problem-icon-large"
                  initial={{ scale: 0, rotate: -180 }}
                  whileInView={{ scale: 1, rotate: 0 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.6, delay: 0.3 + index * 0.2 }}
                >
                  {problem.icon}
                </motion.div>

                <div className="problem-content-text">
                  <motion.h3 
                    className="problem-title-large"
                    initial={{ opacity: 0, y: 20 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    transition={{ duration: 0.6, delay: 0.4 + index * 0.2 }}
                  >
                    {problem.title}
                  </motion.h3>

                  <motion.p 
                    className="problem-description-text"
                    initial={{ opacity: 0, y: 20 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    transition={{ duration: 0.6, delay: 0.5 + index * 0.2 }}
                  >
                    {problem.description}
                  </motion.p>

                  <motion.div 
                    className="problem-fact"
                    initial={{ opacity: 0, scale: 0.8 }}
                    whileInView={{ opacity: 1, scale: 1 }}
                    viewport={{ once: true }}
                    transition={{ duration: 0.5, delay: 0.6 + index * 0.2 }}
                  >
                    {problem.fact}
                  </motion.div>
                </div>
              </motion.div>
            ))}
          </div>

          {/* Transition to Solution */}
          <motion.div 
            className="problem-cta"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.8 }}
          >
            <h3 className="cta-title">Ready to Break Free from These Problems?</h3>
            <p className="cta-description">Let us handle your daily meals while you focus on what matters most</p>
          </motion.div>
        </div>
      </section>

      {/* Features Section - Combined Daily Meals Delivered + Pricing */}
      <section id="features" className="solution-section">
        <div className="solution-content">
          <div className="container mx-auto px-4 md:px-6">
            {/* Header Section - This becomes sticky */}
            <div className={`main-header-section ${stickyState.headerSticky ? 'is-sticky' : ''}`}>
              <div className="text-center mb-4">
                <h2 className="text-3xl md:text-4xl lg:text-5xl font-bold mb-2 text-gray-900 tracking-tight">
                  Myna <span className="text-primary">Kitchen</span>
                </h2>
                <h3 className="text-xl md:text-2xl font-medium mb-4 text-gray-700">
                  Daily Meals Delivered
                </h3>
                <p className="text-lg text-gray-600 max-w-2xl mx-auto">
                  With us you no longer have to run a kitchen, manage groceries or worry about your daily food.
                </p>
              </div>

              {/* Pricing Highlight */}
              <motion.div 
                className="text-center bg-transparent rounded-3xl p-8 mb-16"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.8, delay: 0.2 }}
              >
                <div className="text-primary text-lg font-medium mb-2">Starting at</div>
                <div className="text-4xl md:text-5xl font-bold text-gray-900 mb-2">â‚¹120<span className="text-xl text-gray-600">/day</span></div>
                <p className="text-gray-600 mb-6">Fresh meals delivered to your doorstep</p>

                <button 
                  className="pricing-button bg-gradient-to-r from-primary to-orange-600 hover:from-primary/90 hover:to-orange-600/90 text-white font-medium px-8 py-3 rounded-full shadow-lg transition-all transform hover:scale-105"
                  onClick={() => {
                    const section = document.getElementById('subscription-plans');
                    if (section) {
                      window.scrollTo({
                        top: section.offsetTop - 80,
                        behavior: 'smooth'
                      });
                    }
                  }}
                >
                  Check Plans and Pricing
                </button>
              </motion.div>
            </div>

            {/* Sticky Line for Cells */}
            <div className={`sticky-line ${stickyState.cellsSticky ? 'is-active' : ''}`}>
              <div className="sticky-line-content">
                {stickyState.cellsSticky && (
                  <>
                    <div className="stacked-cell-preview">
                      <div className="flex items-center justify-center space-x-4">
                        <div className={`w-12 h-12 ${solutionFeatures[stickyState.activeCell].iconBg} rounded-xl flex items-center justify-center shadow-sm`}>
                          <span className="text-lg">{solutionFeatures[stickyState.activeCell].icon}</span>
                        </div>
                        <div className="text-center">
                          <h3 className="text-lg font-bold text-gray-900 mb-1">
                            {solutionFeatures[stickyState.activeCell].title}
                          </h3>
                          <p className="text-sm text-gray-600 max-w-md">
                            {solutionFeatures[stickyState.activeCell].description}
                          </p>
                        </div>
                      </div>
                    </div>
                    <div className="scroll-indicator">
                      <div className="text-xs text-gray-500 mt-2 flex items-center justify-center space-x-1">
                        <span>Scroll to see more</span>
                        <svg className="w-4 h-4 animate-bounce" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 14l-7 7m0 0l-7-7m7 7V3" />
                        </svg>
                      </div>
                    </div>
                  </>
                )}
              </div>
            </div>

            {/* Single Column Cell Layout */}
            <div className="single-column-container">
              {solutionFeatures.map((feature, index) => (
                <div key={index} className={`cell-wrapper cell-${index + 1}`}>
                  <motion.div
                    className={`modern-tile single-column-tile relative overflow-hidden rounded-2xl ${feature.bgColor} border border-white/50 shadow-lg backdrop-blur-sm ${
                      stickyState.cellsSticky && index === stickyState.activeCell ? 'is-stacked' : ''
                    }`}
                    initial={{ opacity: 0, y: 20 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    viewport={{ once: true }}
                    transition={{ duration: 0.6, delay: 0.1 + index * 0.1 }}
                  >
                    {/* Content */}
                    <div className={`absolute inset-0 bg-gradient-to-br ${feature.gradient} opacity-5`}></div>
                    <div className="relative z-10 p-8 h-full flex flex-col">
                      <div className={`w-16 h-16 ${feature.iconBg} rounded-2xl flex items-center justify-center mb-6 shadow-sm`}>
                        <span className="text-2xl">{feature.icon}</span>
                      </div>
                      <h3 className="text-xl font-bold mb-4 text-gray-900 leading-tight">
                        {feature.title}
                      </h3>
                      <p className="text-gray-600 leading-relaxed flex-grow">
                        {feature.description}
                      </p>
                      <div className={`mt-6 h-1 w-12 bg-gradient-to-r ${feature.gradient} rounded-full`}></div>
                    </div>
                    <div className="absolute inset-0 bg-white/10 opacity-0 hover:opacity-100 transition-opacity duration-300"></div>
                  </motion.div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default ProblemStatement;
