import React, { useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import './StickyCards.css';

const StickyCard = ({ card, index, totalCards, containerRef }) => {
  const { scrollYProgress } = useScroll({
    target: containerRef,
    offset: ["start start", "end start"]
  });

  const targetScale = 1 - ((totalCards - index) * 0.05);
  const range = [index * 0.25, 1];
  const scale = useTransform(scrollYProgress, range, [1, targetScale]);
  const opacity = useTransform(scrollYProgress, range, [1, 0.8]);

  return (
    <motion.div
      style={{ 
        scale,
        opacity,
        position: index === 0 ? 'relative' : 'sticky',
        top: index === 0 ? 'auto' : `${60 + index * 20}px`,
        zIndex: totalCards - index
      }}
      className="w-full px-4 mb-8"
    >
      <div className="max-w-6xl mx-auto">
        <motion.div
          className={`relative overflow-hidden rounded-3xl bg-gradient-to-br ${card.gradient} shadow-2xl`}
          initial={{ opacity: 0, y: 100 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true, margin: "-100px" }}
          transition={{ duration: 0.8, delay: index * 0.1 }}
          whileHover={{ y: -10, transition: { duration: 0.3 } }}
        >
          {/* Background Pattern */}
          <div className="absolute inset-0 opacity-10">
            <div className="absolute inset-0 bg-gradient-to-br from-white/20 to-transparent"></div>
            <div className="absolute top-0 right-0 w-96 h-96 bg-white/10 rounded-full -translate-y-48 translate-x-48"></div>
            <div className="absolute bottom-0 left-0 w-64 h-64 bg-white/10 rounded-full translate-y-32 -translate-x-32"></div>
          </div>

          <div className="relative z-10 p-8 md:p-12 lg:p-16">
            {/* Header */}
            <div className="flex items-center mb-8">
              <div className="text-6xl mr-6 bg-white/20 backdrop-blur-sm rounded-2xl p-4">
                {card.icon}
              </div>
              <div>
                <h3 className="text-4xl md:text-5xl font-bold text-white mb-2">
                  {card.title}
                </h3>
                <p className="text-xl text-white/90 font-medium">
                  {card.subtitle}
                </p>
              </div>
            </div>

            {/* Content Grid */}
            <div className="grid md:grid-cols-2 gap-8 lg:gap-12">
              {/* Description */}
              <div>
                <p className="text-lg text-white/95 leading-relaxed mb-8">
                  {card.description}
                </p>

                {/* Features */}
                <div className="grid grid-cols-2 gap-4">
                  {card.features.map((feature, featureIndex) => (
                    <motion.div
                      key={featureIndex}
                      className="flex items-center space-x-3 bg-white/10 backdrop-blur-sm rounded-xl p-3"
                      initial={{ opacity: 0, x: -20 }}
                      whileInView={{ opacity: 1, x: 0 }}
                      viewport={{ once: true }}
                      transition={{ duration: 0.5, delay: (index * 0.1) + (featureIndex * 0.1) }}
                    >
                      <div className="w-2 h-2 bg-white rounded-full"></div>
                      <span className="text-white font-medium text-sm">
                        {feature}
                      </span>
                    </motion.div>
                  ))}
                </div>
              </div>

              {/* Visual Element */}
              <div className="flex items-center justify-center">
                <motion.div
                  className="relative w-64 h-64 bg-white/10 backdrop-blur-sm rounded-3xl flex items-center justify-center"
                  whileHover={{ rotate: 5, scale: 1.05 }}
                  transition={{ duration: 0.3 }}
                >
                  <div className="text-8xl opacity-50">
                    {card.icon}
                  </div>
                  <div className="absolute inset-0 bg-gradient-to-br from-white/20 to-transparent rounded-3xl"></div>
                </motion.div>
              </div>
            </div>

            {/* Action Button */}
            <motion.div 
              className="mt-12 flex justify-center"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.6, delay: index * 0.1 + 0.3 }}
            >
              <motion.button
                className="bg-white text-gray-900 px-8 py-4 rounded-2xl font-bold text-lg shadow-xl hover:shadow-2xl transition-all duration-300"
                whileHover={{ scale: 1.05, y: -2 }}
                whileTap={{ scale: 0.95 }}
              >
                Learn More
              </motion.button>
            </motion.div>
          </div>
        </motion.div>
      </div>
    </motion.div>
  );
};

const StickyCards = () => {
  const containerRef = useRef(null);

  const cardsData = [
    {
      id: 1,
      title: "Premium Quality Ingredients",
      subtitle: "Farm Fresh & Organic",
      description: "We source only the finest ingredients from local farms and trusted suppliers. Every meal is crafted with organic vegetables, premium proteins, and authentic spices to deliver exceptional taste and nutrition.",
      gradient: "from-emerald-500 to-teal-600",
      icon: "üå±",
      features: ["100% Organic", "Farm Fresh", "No Preservatives", "Locally Sourced"]
    },
    {
      id: 2,
      title: "Expert Chef Preparation",
      subtitle: "Culinary Excellence",
      description: "Our team of experienced chefs brings years of culinary expertise to every dish. Each meal is carefully prepared using traditional cooking methods combined with modern techniques for the perfect balance of flavor and nutrition.",
      gradient: "from-orange-500 to-red-600",
      icon: "üë®‚Äçüç≥",
      features: ["Expert Chefs", "Traditional Methods", "Modern Techniques", "Balanced Nutrition"]
    },
    {
      id: 3,
      title: "Flexible Delivery Options",
      subtitle: "Convenience at Your Doorstep",
      description: "Choose from multiple delivery time slots that fit your schedule. Our reliable delivery network ensures your meals arrive fresh and on time, every single day. Skip, pause, or modify your plan anytime.",
      gradient: "from-blue-500 to-purple-600",
      icon: "üöö",
      features: ["Multiple Time Slots", "Daily Delivery", "Flexible Plans", "Always Fresh"]
    },
    {
      id: 4,
      title: "Satisfaction Guaranteed",
      subtitle: "Your Happiness Matters",
      description: "We stand behind every meal we deliver. If you're not completely satisfied, we'll make it right. Our customer support team is available 24/7 to ensure you have the best experience possible.",
      gradient: "from-pink-500 to-rose-600",
      icon: "üíØ",
      features: ["100% Satisfaction", "24/7 Support", "Money Back Guarantee", "Quality Assured"]
    }
  ];

  return (
    <section className="relative bg-gray-50 min-h-screen">
      {/* Section Header */}
      <div className="text-center py-20 px-4">
        <motion.h2 
          className="text-5xl md:text-6xl font-bold text-gray-900 mb-6"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          Why Choose Myna Kitchen
        </motion.h2>
        <motion.p 
          className="text-xl text-gray-600 max-w-3xl mx-auto"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
          Discover what makes us the perfect choice for your daily meal needs
        </motion.p>
      </div>

      {/* Sticky Cards Container */}
      <div ref={containerRef} className="relative">
        {cardsData.map((card, index) => (
          <StickyCard
            key={card.id}
            card={card}
            index={index}
            totalCards={cardsData.length}
            containerRef={containerRef}
          />
        ))}
      </div>

      {/* Bottom Spacing */}
      <div className="h-96"></div>
    </section>
  );
};

export default StickyCards;
