import React from 'react';
import { motion } from 'framer-motion';
import './ProblemStatement.css';

const ProblemStatement = () => {
  const problems = [
    {
      id: 'busy',
      title: 'Too Busy to Cook',
      description: 'Your schedule is packed. Between work, commute, and life, who has time to plan, shop, and cook?',
      icon: '‚è∞',
      fact: 'Average person works 8+ hours daily'
    },
    {
      id: 'junk',
      title: 'Stuck with Junk Food',
      description: 'Quick fixes lead to unhealthy choices. Fast food becomes the norm when convenience matters most.',
      icon: 'üçî',
      fact: '70% of daily intake is processed food'
    },
    {
      id: 'expensive',
      title: 'Expensive & Wasteful',
      description: 'Ordering out daily burns through your budget. Groceries expire unused. Money down the drain.',
      icon: 'üí∏',
      fact: 'Spend ‚Çπ500+ daily on food delivery'
    },
    {
      id: 'away',
      title: 'Away from Home',
      description: 'Living away from family means no home-cooked meals. Missing that comfort and nutrition.',
      icon: 'üè†',
      fact: 'Missing the comfort of home-cooked meals'
    }
  ];

  const solutionFeatures = [
    {
      icon: 'üçΩÔ∏è',
      title: 'Daily Meals Delivered',
      description: 'Fresh, home-style meals prepared daily and delivered right to your doorstep',
      gradient: 'from-blue-500 to-purple-600',
      bgColor: 'bg-blue-50',
      iconBg: 'bg-blue-100'
    },
    {
      icon: '‚è∞',
      title: 'Save Your Time',
      description: 'No more planning, shopping, or cooking. We handle everything while you focus on what matters',
      gradient: 'from-green-500 to-teal-600',
      bgColor: 'bg-green-50',
      iconBg: 'bg-green-100'
    },
    {
      icon: 'üí∞',
      title: 'Budget-Friendly',
      description: 'Starting at ‚Çπ120/day - more affordable than ordering out, with zero waste',
      gradient: 'from-yellow-500 to-orange-600',
      bgColor: 'bg-yellow-50',
      iconBg: 'bg-yellow-100'
    },
    {
      icon: 'üè†',
      title: 'Home-Style Comfort',
      description: 'Taste the comfort of home-cooked meals, even when you\'re away from family',
      gradient: 'from-pink-500 to-red-600',
      bgColor: 'bg-pink-50',
      iconBg: 'bg-pink-100'
    }
  ];

  return (
    <section id="problem-statement" className="problem-section">
      <div className="container mx-auto px-4 md:px-6">
        {/* Problem Section Header */}
        <motion.div 
          className="text-center mb-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          <motion.h2 
            className="problem-title"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            We Know Your <span className="text-red-600">Daily Struggle</span>
          </motion.h2>
          <motion.p 
            className="problem-subtitle"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            Modern life shouldn't mean compromising on nutrition and taste
          </motion.p>
        </motion.div>

        {/* Problems List */}
        <div className="problems-list">
          {problems.map((problem, index) => (
            <motion.div
              key={problem.id}
              className="problem-item"
              initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
              whileInView={{ opacity: 1, x: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.8, delay: index * 0.2 }}
            >
              <motion.div 
                className="problem-icon-large"
                initial={{ scale: 0, rotate: -180 }}
                whileInView={{ scale: 1, rotate: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.6, delay: 0.3 + index * 0.2 }}
              >
                {problem.icon}
              </motion.div>

              <div className="problem-content-text">
                <motion.h3 
                  className="problem-title-large"
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.6, delay: 0.4 + index * 0.2 }}
                >
                  {problem.title}
                </motion.h3>

                <motion.p 
                  className="problem-description-text"
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.6, delay: 0.5 + index * 0.2 }}
                >
                  {problem.description}
                </motion.p>

                <motion.div 
                  className="problem-fact"
                  initial={{ opacity: 0, scale: 0.8 }}
                  whileInView={{ opacity: 1, scale: 1 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.5, delay: 0.6 + index * 0.2 }}
                >
                  {problem.fact}
                </motion.div>
              </div>
            </motion.div>
          ))}
        </div>

        {/* Transition to Solution */}
        <motion.div 
          className="problem-cta"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8, delay: 0.8 }}
        >
          <h3 className="cta-title">Ready to Break Free from These Problems?</h3>
          <p className="cta-description">Let us handle your daily meals while you focus on what matters most</p>
        </motion.div>
      </div>

      {/* Simple Solution Section - No Sticky, No Animations */}
      <div className="solution-section bg-gradient-to-br from-gray-50 via-white to-blue-50 py-20">
        <div className="container mx-auto px-4 md:px-6">
          {/* Header */}
          <div className="text-center mb-16">
            <h2 className="text-3xl md:text-4xl lg:text-5xl font-bold mb-4 text-gray-900 tracking-tight">
              Daily Meals <span className="text-primary">Delivered</span>
            </h2>
            <p className="text-lg text-gray-600 max-w-2xl mx-auto">
              With us you no longer have to run a kitchen, manage groceries or worry about your daily food.
            </p>
          </div>

          {/* Four Tiles in Diagonal 4x4 Layout */}
          <div className="diagonal-grid-container mb-16">
            {/* Row 1 - Box 1 in first column */}
            <div className="diagonal-grid">
              <div className="grid-cell">
                <div className={`modern-tile relative overflow-hidden rounded-2xl ${solutionFeatures[0].bgColor} border border-white/50 shadow-lg backdrop-blur-sm`}>
                  {/* Gradient overlay */}
                  <div className={`absolute inset-0 bg-gradient-to-br ${solutionFeatures[0].gradient} opacity-5`}></div>

                  {/* Content */}
                  <div className="relative z-10 p-8 h-full flex flex-col">
                    {/* Icon */}
                    <div className={`w-16 h-16 ${solutionFeatures[0].iconBg} rounded-2xl flex items-center justify-center mb-6 shadow-sm`}>
                      <span className="text-2xl">{solutionFeatures[0].icon}</span>
                    </div>

                    {/* Title */}
                    <h3 className="text-xl font-bold mb-4 text-gray-900 leading-tight">
                      {solutionFeatures[0].title}
                    </h3>

                    {/* Description */}
                    <p className="text-gray-600 leading-relaxed flex-grow">
                      {solutionFeatures[0].description}
                    </p>

                    {/* Decorative element */}
                    <div className={`mt-6 h-1 w-12 bg-gradient-to-r ${solutionFeatures[0].gradient} rounded-full`}></div>
                  </div>

                  {/* Hover effect overlay */}
                  <div className="absolute inset-0 bg-white/10 opacity-0 hover:opacity-100 transition-opacity duration-300"></div>
                </div>
              </div>
              <div className="grid-cell"></div>
              <div className="grid-cell"></div>
              <div className="grid-cell"></div>

              {/* Row 2 - Box 2 in second column */}
              <div className="grid-cell"></div>
              <div className="grid-cell">
                <div className={`modern-tile relative overflow-hidden rounded-2xl ${solutionFeatures[1].bgColor} border border-white/50 shadow-lg backdrop-blur-sm`}>
                  {/* Gradient overlay */}
                  <div className={`absolute inset-0 bg-gradient-to-br ${solutionFeatures[1].gradient} opacity-5`}></div>

                  {/* Content */}
                  <div className="relative z-10 p-8 h-full flex flex-col">
                    {/* Icon */}
                    <div className={`w-16 h-16 ${solutionFeatures[1].iconBg} rounded-2xl flex items-center justify-center mb-6 shadow-sm`}>
                      <span className="text-2xl">{solutionFeatures[1].icon}</span>
                    </div>

                    {/* Title */}
                    <h3 className="text-xl font-bold mb-4 text-gray-900 leading-tight">
                      {solutionFeatures[1].title}
                    </h3>

                    {/* Description */}
                    <p className="text-gray-600 leading-relaxed flex-grow">
                      {solutionFeatures[1].description}
                    </p>

                    {/* Decorative element */}
                    <div className={`mt-6 h-1 w-12 bg-gradient-to-r ${solutionFeatures[1].gradient} rounded-full`}></div>
                  </div>

                  {/* Hover effect overlay */}
                  <div className="absolute inset-0 bg-white/10 opacity-0 hover:opacity-100 transition-opacity duration-300"></div>
                </div>
              </div>
              <div className="grid-cell"></div>
              <div className="grid-cell"></div>

              {/* Row 3 - Box 3 in third column */}
              <div className="grid-cell"></div>
              <div className="grid-cell"></div>
              <div className="grid-cell">
                <div className={`modern-tile relative overflow-hidden rounded-2xl ${solutionFeatures[2].bgColor} border border-white/50 shadow-lg backdrop-blur-sm`}>
                  {/* Gradient overlay */}
                  <div className={`absolute inset-0 bg-gradient-to-br ${solutionFeatures[2].gradient} opacity-5`}></div>

                  {/* Content */}
                  <div className="relative z-10 p-8 h-full flex flex-col">
                    {/* Icon */}
                    <div className={`w-16 h-16 ${solutionFeatures[2].iconBg} rounded-2xl flex items-center justify-center mb-6 shadow-sm`}>
                      <span className="text-2xl">{solutionFeatures[2].icon}</span>
                    </div>

                    {/* Title */}
                    <h3 className="text-xl font-bold mb-4 text-gray-900 leading-tight">
                      {solutionFeatures[2].title}
                    </h3>

                    {/* Description */}
                    <p className="text-gray-600 leading-relaxed flex-grow">
                      {solutionFeatures[2].description}
                    </p>

                    {/* Decorative element */}
                    <div className={`mt-6 h-1 w-12 bg-gradient-to-r ${solutionFeatures[2].gradient} rounded-full`}></div>
                  </div>

                  {/* Hover effect overlay */}
                  <div className="absolute inset-0 bg-white/10 opacity-0 hover:opacity-100 transition-opacity duration-300"></div>
                </div>
              </div>
              <div className="grid-cell"></div>

              {/* Row 4 - Box 4 in fourth column */}
              <div className="grid-cell"></div>
              <div className="grid-cell"></div>
              <div className="grid-cell"></div>
              <div className="grid-cell">
                <div className={`modern-tile relative overflow-hidden rounded-2xl ${solutionFeatures[3].bgColor} border border-white/50 shadow-lg backdrop-blur-sm`}>
                  {/* Gradient overlay */}
                  <div className={`absolute inset-0 bg-gradient-to-br ${solutionFeatures[3].gradient} opacity-5`}></div>

                  {/* Content */}
                  <div className="relative z-10 p-8 h-full flex flex-col">
                    {/* Icon */}
                    <div className={`w-16 h-16 ${solutionFeatures[3].iconBg} rounded-2xl flex items-center justify-center mb-6 shadow-sm`}>
                      <span className="text-2xl">{solutionFeatures[3].icon}</span>
                    </div>

                    {/* Title */}
                    <h3 className="text-xl font-bold mb-4 text-gray-900 leading-tight">
                      {solutionFeatures[3].title}
                    </h3>

                    {/* Description */}
                    <p className="text-gray-600 leading-relaxed flex-grow">
                      {solutionFeatures[3].description}
                    </p>

                    {/* Decorative element */}
                    <div className={`mt-6 h-1 w-12 bg-gradient-to-r ${solutionFeatures[3].gradient} rounded-full`}></div>
                  </div>

                  {/* Hover effect overlay */}
                  <div className="absolute inset-0 bg-white/10 opacity-0 hover:opacity-100 transition-opacity duration-300"></div>
                </div>
              </div>
            </div>
          </div>

          {/* Pricing Highlight */}
          <div className="text-center bg-white/80 backdrop-blur-sm rounded-3xl p-8 border border-white/50 shadow-xl">
            <div className="text-primary text-lg font-medium mb-2">Starting at</div>
            <div className="text-4xl md:text-5xl font-bold text-gray-900 mb-2">‚Çπ120<span className="text-xl text-gray-600">/day</span></div>
            <p className="text-gray-600 mb-6">Fresh meals delivered to your doorstep</p>

            <button 
              className="bg-gradient-to-r from-primary to-orange-600 hover:from-primary/90 hover:to-orange-600/90 text-white font-medium px-8 py-3 rounded-full shadow-lg transition-all transform hover:scale-105"
              onClick={() => {
                const section = document.getElementById('subscription-plans');
                if (section) {
                  window.scrollTo({
                    top: section.offsetTop - 80,
                    behavior: 'smooth'
                  });
                }
              }}
            >
              Get Started
            </button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default ProblemStatement;
