import React, { useEffect, useRef, useState } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import './ProblemStatement.css';

const ProblemStatement = () => {
  const solutionRef = useRef(null);
  const [currentTile, setCurrentTile] = useState(0);

  const { scrollYProgress } = useScroll({
    target: solutionRef, // Target the tall container for scroll progress
    offset: ["start start", "end end"] // Progress from when top of container hits top of viewport, to when bottom hits bottom
  });

  // Transform scroll progress to tile progression (0 to number of features)
  // Each step (0 to 1, 1 to 2, etc.) corresponds to a tile appearing
  const tileProgress = useTransform(
    scrollYProgress,
    [0, 0.8], // Animate tiles over the first 80% of the scroll section's height
    [0, solutionFeatures.length] // Map to 0 to 4 (if 4 features)
  );

  useEffect(() => {
    const unsubscribe = tileProgress.onChange((latest) => {
      setCurrentTile(Math.floor(latest)); // Get the current step (0, 1, 2, 3, or 4)
    });
    return unsubscribe;
  }, [tileProgress]);

  const problems = [
    {
      id: 'busy',
      title: 'Too Busy to Cook',
      description: 'Your schedule is packed. Between work, commute, and life, who has time to plan, shop, and cook?',
      icon: '‚è∞',
      fact: 'Average person works 8+ hours daily'
    },
    {
      id: 'junk',
      title: 'Stuck with Junk Food',
      description: 'Quick fixes lead to unhealthy choices. Fast food becomes the norm when convenience matters most.',
      icon: 'üçî',
      fact: '70% of daily intake is processed food'
    },
    {
      id: 'expensive',
      title: 'Expensive & Wasteful',
      description: 'Ordering out daily burns through your budget. Groceries expire unused. Money down the drain.',
      icon: 'üí∏',
      fact: 'Spend ‚Çπ500+ daily on food delivery'
    },
    {
      id: 'away',
      title: 'Away from Home',
      description: 'Living away from family means no home-cooked meals. Missing that comfort and nutrition.',
      icon: 'üè†',
      fact: 'Missing the comfort of home-cooked meals'
    }
  ];

  const solutionFeatures = [
    {
      icon: 'üçΩÔ∏è',
      title: 'Daily Meals Delivered',
      description: 'Fresh, home-style meals prepared daily and delivered right to your doorstep',
      gradient: 'from-blue-500 to-purple-600',
      bgColor: 'bg-blue-50',
      iconBg: 'bg-blue-100'
    },
    {
      icon: '‚è∞',
      title: 'Save Your Time',
      description: 'No more planning, shopping, or cooking. We handle everything while you focus on what matters',
      gradient: 'from-green-500 to-teal-600',
      bgColor: 'bg-green-50',
      iconBg: 'bg-green-100'
    },
    {
      icon: 'üí∞',
      title: 'Budget-Friendly',
      description: 'Starting at ‚Çπ120/day - more affordable than ordering out, with zero waste',
      gradient: 'from-yellow-500 to-orange-600',
      bgColor: 'bg-yellow-50',
      iconBg: 'bg-yellow-100'
    },
    {
      icon: 'üè†',
      title: 'Home-Style Comfort',
      description: 'Taste the comfort of home-cooked meals, even when you\'re away from family',
      gradient: 'from-pink-500 to-red-600',
      bgColor: 'bg-pink-50',
      iconBg: 'bg-pink-100'
    }
  ];

  // Calculate the total height needed for the sticky section
  // Each feature tile + the final pricing highlight should have about a screen height to animate in.
  const stickySectionHeight = `${(solutionFeatures.length + 1) * 100}vh`; // e.g., 500vh for 4 features + pricing

  return (
    <section id="problem-statement" className="problem-section">
      <div className="container mx-auto px-4 md:px-6">
        {/* Problem Section Header */}
        <motion.div 
          className="text-center mb-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          <motion.h2 
            className="problem-title"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            We Know Your <span className="text-red-600">Daily Struggle</span>
          </motion.h2>
          <motion.p 
            className="problem-subtitle"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            Modern life shouldn't mean compromising on nutrition and taste
          </motion.p>
        </motion.div>

        {/* Problems List */}
        <div className="problems-list">
          {problems.map((problem, index) => (
            <motion.div
              key={problem.id}
              className="problem-item"
              initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
              whileInView={{ opacity: 1, x: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.8, delay: index * 0.2 }}
            >
              <motion.div 
                className="problem-icon-large"
                initial={{ scale: 0, rotate: -180 }}
                whileInView={{ scale: 1, rotate: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.6, delay: 0.3 + index * 0.2 }}
              >
                {problem.icon}
              </motion.div>

              <div className="problem-content-text">
                <motion.h3 
                  className="problem-title-large"
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.6, delay: 0.4 + index * 0.2 }}
                >
                  {problem.title}
                </motion.h3>

                <motion.p 
                  className="problem-description-text"
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.6, delay: 0.5 + index * 0.2 }}
                >
                  {problem.description}
                </motion.p>

                <motion.div 
                  className="problem-fact"
                  initial={{ opacity: 0, scale: 0.8 }}
                  whileInView={{ opacity: 1, scale: 1 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.5, delay: 0.6 + index * 0.2 }}
                >
                  {problem.fact}
                </motion.div>
              </div>
            </motion.div>
          ))}
        </div>

        {/* Transition to Solution */}
        <motion.div 
          className="problem-cta"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8, delay: 0.8 }}
        >
          <h3 className="cta-title">Ready to Break Free from These Problems?</h3>
          <p className="cta-description">Let us handle your daily meals while you focus on what matters most</p>
        </motion.div>
      </div>

      {/* Modern Sticky Solution Section - Tall scrollable container */}
      <div ref={solutionRef} className="sticky-solution-section relative" style={{ height: stickySectionHeight }}>
        {/* Background with gradient - covers the entire tall section */}
        <div className="absolute inset-0 bg-gradient-to-br from-gray-50 via-white to-blue-50"></div>

        {/* Sticky container - this is what sticks to the top and has h-screen */}
        <div className="sticky top-0 h-screen flex flex-col justify-center py-16 md:py-20 overflow-hidden">
          <div className="container mx-auto px-4 md:px-6">
            {/* Solution Header - Always visible once sticky */}
            <motion.div 
              className="text-center mb-10 md:mb-12 relative z-10"
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.1, type: 'spring', stiffness: 50 }}
            >
              <h2 
                className="text-3xl md:text-4xl lg:text-5xl font-bold mb-3 md:mb-4 text-gray-900 tracking-tight"
              >
                Daily Meals <span className="text-primary">Delivered</span>
              </h2>
              <p 
                className="text-lg text-gray-600 max-w-2xl mx-auto"
              >
                With us you no longer have to run a kitchen, manage groceries or worry about your daily food.
              </p>
            </motion.div>

            {/* Modern Tiles Grid */}
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 md:gap-6 mb-10 md:mb-12">
              {solutionFeatures.map((feature, index) => (
                <motion.div
                  key={index}
                  className={`modern-tile relative overflow-hidden rounded-2xl ${feature.bgColor} border border-white/50 shadow-lg backdrop-blur-sm`}
                  initial={{ opacity: 0, y: 150, scale: 0.7 }}
                  animate={{
                    opacity: currentTile >= index + 1 ? 1 : 0.3, // Fade in fully when it's this tile's turn or past
                    y: currentTile >= index + 1 ? 0 : 150,
                    scale: currentTile >= index + 1 ? 1 : 0.7,
                  }}
                  transition={{
                    type: "spring",
                    stiffness: 70,
                    damping: 15,
                    delay: currentTile >= index + 1 ? 0 : 0.1 * index // Stagger initial hidden states slightly
                  }}
                  whileHover={{ 
                    scale: 1.03,
                    y: -8,
                    transition: { type: "spring", stiffness: 200, damping: 10 }
                  }}
                >
                  {/* Gradient overlay */}
                  <div className={`absolute inset-0 bg-gradient-to-br ${feature.gradient} opacity-5`}></div>

                  {/* Content */}
                  <div className="relative z-10 p-6 md:p-8 h-full flex flex-col">
                    {/* Icon */}
                    <div className={`w-12 h-12 md:w-16 md:h-16 ${feature.iconBg} rounded-xl md:rounded-2xl flex items-center justify-center mb-4 md:mb-6 shadow-sm`}>
                      <span className="text-xl md:text-2xl">{feature.icon}</span>
                    </div>

                    {/* Title */}
                    <h3 className="text-lg md:text-xl font-bold mb-3 md:mb-4 text-gray-900 leading-tight">
                      {feature.title}
                    </h3>

                    {/* Description */}
                    <p className="text-sm md:text-base text-gray-600 leading-relaxed flex-grow">
                      {feature.description}
                    </p>

                    {/* Decorative element */}
                    <div className={`mt-4 md:mt-6 h-1 w-10 md:w-12 bg-gradient-to-r ${feature.gradient} rounded-full`}></div>
                  </div>

                  {/* Hover effect overlay */}
                  <div className="absolute inset-0 bg-white/10 opacity-0 hover:opacity-100 transition-opacity duration-300"></div>
                </motion.div>
              ))}
            </div>

            {/* Pricing Highlight */}
            <motion.div 
              className="text-center bg-white/80 backdrop-blur-sm rounded-2xl md:rounded-3xl p-6 md:p-8 border border-white/50 shadow-xl relative z-10 max-w-2xl mx-auto"
              initial={{ opacity: 0, scale: 0.9 }}
              animate={{
                opacity: currentTile >= solutionFeatures.length ? 1 : 0, // Appear after all tiles
                scale: currentTile >= solutionFeatures.length ? 1 : 0.9,
              }}
              transition={{ type: "spring", stiffness: 70, damping: 15, delay: 0.2 }}
            >
              <div className="text-primary text-base md:text-lg font-medium mb-1 md:mb-2">Starting at</div>
              <div className="text-3xl md:text-4xl lg:text-5xl font-bold text-gray-900 mb-1 md:mb-2">‚Çπ120<span className="text-lg md:text-xl text-gray-600">/day</span></div>
              <p className="text-sm md:text-base text-gray-600 mb-4 md:mb-6">Fresh meals delivered to your doorstep</p>

              <motion.button 
                className="bg-gradient-to-r from-primary to-orange-600 hover:from-primary/90 hover:to-orange-600/90 text-white font-medium px-6 py-2.5 md:px-8 md:py-3 rounded-full shadow-lg transition-all transform hover:scale-105"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => {
                  const section = document.getElementById('subscription-plans');
                  if (section) {
                    window.scrollTo({
                      top: section.offsetTop - 80,
                      behavior: 'smooth'
                    });
                  }
                }}
              >
                Get Started
              </motion.button>
            </motion.div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default ProblemStatement;
